import Button from "react-bootstrap/Button";

import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { deleteCat } from "../../rtk/slices/categoriesSlice";
import ListGroup from "react-bootstrap/ListGroup";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import CloseButton from "react-bootstrap/CloseButton";
import AddProduct from "../AddProduct";
import Form from "react-bootstrap/Form";
import { updateSize } from "../../rtk/slices/sizesSlice";
import { useNavigate } from "react-router-dom";

const SizesTable = ({ formState, setForm }) => {
  const dispatch = useDispatch();
  const sizes = useSelector((state) => state.sizes);
  const HandleUpdate = (e) => {
    dispatch(
      updateSize({
        id: e.id,
        colorName: e.name,
        elementState: e.checked,
      })
    );
    navigate("/Dashboard/MangeColors");
  };
  return (
    <>
      <Table>
        <thead>
          <tr>
            <th>#</th>
            <th>SizeName</th>
            <th>State</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {sizes.map((size) => (
            <tr key={size.id}>
              <td style={{ width: " 20px" }}>{size.id}</td>
              <td>{size.sizeName}</td>
              <td>
                {" "}
                <Form>
                  <Form.Check
                    type="switch"
                    id={size.id}
                    label="Active"
                    name={size.colorName}
                    checked={size.elementState === true ? true : false}
                    onClick={(e) => HandleUpdate(e.target)}
                  />
                </Form>
              </td>

              <td>
                {" "}
                <span className="delete-section">
                  <CloseButton
                    onClick={() => {
                      dispatch(deleteCat(size));
                    }}
                  />
                </span>
                <span className="edit-section">
                  <Link to={`/Dashboard/MangeSizes/Update/${size.id}`}>
                    <i className="fa-regular fa-pen-to-square"></i>
                  </Link>
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <AddProduct formState={formState} setForm={setForm} />
    </>
  );
};

export default SizesTable;
