import { createSlice } from "@reduxjs/toolkit";
const productsSlice = createSlice({
  name: "productsSlice",
  initialState: [],
  reducers: {
    addToCart: (state, action) => {
      const finded = state.find((product) => product.id === action.payload.id);
      if (finded) {
        finded.qnt += 1;
      } else {
        const cart = { ...action.payload, qnt: 1 };
        state.push(cart);
      }
    },
    deleteCart: (state, action) => {
      const filter = state.filter((el) => el.id !== action.payload);
      return filter;
    },
    clearCart: (state, action) => {
      return (state = []);
    },
    updateCart: (state, action) => {},
  },
});

export const { addToCart, deleteCart, clearCart } = productsSlice.actions;
export default productsSlice.reducer;
