import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useState } from "react";
// import { update } from "../../rtk/slices/productsSlice";

function UpdatePro() {
  const dispatch = useDispatch();
  const params = useParams();
  const navigate = useNavigate();
  console.log(params.id);
  const { products } = useSelector((state) => state.products);
  console.log(products);
  const selectedProduct = products.filter((user) => user._id === params.id);
  const { name, description, categories, price, image } = selectedProduct[0];
  console.log(name);
  const [value, setValue] = useState({
    name,
    price,
    description,
    categories,
    image,
  });
  console.log(value);
  const HandleUpdate = () => {
    // dispatch(
    //   update({
    //     id: params.id,
    //     productName: value.productName,
    //     price: value.price,
    //     category: value.category,
    //     img: "./img/" + value.img.slice(12),
    //   })
    // );
    navigate("/");
  };
  return (
    <div className="editForm">
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            value={value.name}
            onChange={(e) => setValue({ ...value, name: e.target.value })}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Description</Form.Label>
          <Form.Control
            type="text"
            value={value.description}
            onChange={(e) =>
              setValue({ ...value, description: e.target.value })
            }
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Price</Form.Label>
          <Form.Control
            type="text"
            value={value.price}
            onChange={(e) => setValue({ ...value, price: e.target.value })}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Categories</Form.Label>
          <Form.Control
            type="text"
            value={value.categories}
            onChange={(e) => setValue({ ...value, categories: e.target.value })}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Image</Form.Label>
          <Form.Control
            type="file"
            onChange={(e) => setValue({ ...value, image: e.target.value })}
          />
        </Form.Group>

        <Button variant="primary" onClick={() => HandleUpdate()}>
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default UpdatePro;
