import Button from "react-bootstrap/Button";
import { useDispatch } from "react-redux";
import "./AddProduct.css";
import Form from "react-bootstrap/Form";
import { insert } from "../rtk/slices/productsSlice";
import { insertCategory } from "../rtk/slices/categoriesSlice";
import { insertSize } from "../rtk/slices/sizesSlice";
import { insertColor } from "../rtk/slices/colorsSlice";
import { useRef } from "react";
import { useNavigate } from "react-router-dom";
import CloseButton from "react-bootstrap/CloseButton";
import ReactDOM from "react-dom";
import React, { useContext, useState } from "react";
import axios from "axios";

import "./update/UpdateCart.css";
function AddProduct({ formState, setForm }) {
  const name = useRef(null);
  const categories = useRef(null);
  const price = useRef(null);
  const image = useRef(null);
  const description = useRef(null);
  const AddProduct = () => {
    const product = {
      name: name.current.value,
      categories: categories.current.value,
      price: price.current.value,
      description: description.current.value,
      image: "./img/" + image.current.value.slice(12),
    };
  };
  const Overlay = () => (
    <div className="overlay" onClick={() => setForm(false)}></div>
  );
  const BackDrop = ({ children }) => <div className="addForm">{children}</div>;
  const fetchData = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("/addProduct", data);
      console.log(res);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };

  return (
    formState && (
      <>
        {ReactDOM.createPortal(
          <>
            <Overlay />
            <BackDrop>
              {window.location.pathname.includes(
                "/dashboard/MangeProducts"
              ) && (
                <form onSubmit={fetchData}>
                  <h3>Sign Up</h3>

                  <div className="mb-3 text-start">
                    <label>First name</label>
                    <input type="text" className="form-control" ref={name} />
                  </div>
                </form>
              )}
            </BackDrop>
          </>,
          document.getElementById("modal")
        )}
      </>
    )
  );
}
export default AddProduct;
