import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { addProduct } from "../rtk/slices/productsSlice";
import { useState } from "react";

import "./Edit.css";
function AddProduct() {
  const dispatch = useDispatch();
  const [productName, productNameSet] = useState("");
  const [category, categorySet] = useState("");
  const [price, priceSet] = useState("");
  const [image, imageSet] = useState("");
  const [product, productSet] = useState({});
  const AddProduct = () => {
    productSet({
      id: Math.random(),
      productName: productName,
      category: category,
      price: price,
      img: image,
    });
    console.log(product);
    dispatch(addProduct(product));
  };
  return (
    <div className="editForm">
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>ProductName</Form.Label>
          <Form.Control
            type="text"
            value={productName}
            onChange={(e) => productNameSet(e.current.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Category</Form.Label>
          <Form.Control
            type="text"
            value={category}
            onChange={(e) => categorySet(e.current.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Price</Form.Label>
          <Form.Control
            type="text"
            value={price}
            onChange={(e) => priceSet(e.current.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Image</Form.Label>
          <Form.Control
            type="file"
            value={image}
            onChange={(e) => imageSet(e.current.value)}
          />
        </Form.Group>

        <Button variant="primary" onClick={() => AddProduct()}>
          Add
        </Button>
      </Form>
    </div>
  );
}

export default AddProduct;
