import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useState } from "react";
import { updateColor } from "../rtk/slices/colorsSlice";

function UpdateColor() {
  const dispatch = useDispatch();
  const params = useParams();
  const navigate = useNavigate();
  const colors = useSelector((state) => state.colors);
  const selectedColor = colors.filter(
    (user) => user.id.toString() === params.id.toString()
  );
  const { colorName, state } = selectedColor[0];
  const [value, setValue] = useState({
    colorName,
    state,
  });
  const HandleUpdate = () => {
    dispatch(
      updateColor({
        id: params.id,
        colorName: value.colorName,
        state: value.state,
      })
    );
    navigate("/Dashboard/MangeColors");
  };
  return (
    <div className="editForm">
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Color Name</Form.Label>
          <Form.Control
            type="text"
            value={value.colorName}
            onChange={(e) => setValue({ ...value, colorName: e.target.value })}
          />

          <Form.Check
            type="switch"
            id="custom-switch"
            label="Active"
            checked={value.state === "true" ? true : false}
            onChange={(e) => setValue({ ...value, state: !e.target.checked })}
          />
        </Form.Group>

        <Button variant="primary" onClick={() => HandleUpdate()}>
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default UpdateColor;
