import { useDispatch, useSelector } from "react-redux";
import React, { useContext, useState, useRef } from "react";
import { useEffect } from "react";
import {
  GET_PRODUCTS,
  UPDATE_PRODUCT,
  DELETE_PRODUCT,
  RESET_PRO,
  SET_UPDATE_PRODUCT,
} from ".././rtk/slices/productsSlice";
import {
  GET_CATEGORIES,
  UPDATE_CATEGORY,
  DELETE_CATEGORY,
  RESET_CAT,
  SET_UPDATE_CATEGORY,
} from ".././rtk/slices/categoriesSlice";

import {
  CLEAR_ALERT,
  DISPLAY_ALERT,
  SETUP_USER_SUCCESS,
} from "../rtk/slices/authSlice";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  GET_COLORS,
  UPDATE_COLOR,
  RESET_COLOR,
  DELETE_COLOR,
  SET_UPDATE_COLOR,
} from ".././rtk/slices/colorsSlice";
import {
  GET_SIZES,
  GET_SIZE,
  UPDATE_SIZE,
  DELETE_SIZE,
  RESET_SIZE,
  SET_UPDATE_SIZE,
} from ".././rtk/slices/sizesSlice";
import { addToCart, GET_CART } from ".././rtk/slices/cartSlice";

const AppContext = React.createContext();

const AppProvider = ({ children }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const url = window.location.pathname;

  const { updatePro, deletedPro } = useSelector((state) => state.products);

  const { updateCat, deletedCat } = useSelector((state) => state.categories);
  const { updateColor, deletedColor } = useSelector((state) => state.colors);
  const { updateSize, deletedSize } = useSelector((state) => state.sizes);
  const clearAlert = () => {
    setTimeout(() => dispatch(CLEAR_ALERT()), 2000);
  };

  const displayAlert = (msg) => {
    dispatch(DISPLAY_ALERT({ alertText: msg }));
    clearAlert();
  };
  const successUser = (user) => {
    dispatch(SETUP_USER_SUCCESS({ user: user }));
    dispatch(SETUP_USER_SUCCESS(true));
    clearAlert();

    setTimeout(() => {
      navigate("/login");
    }, 2000);
  };
  const getProducts = async () => {
    const res = await axios.get("/dashboard/MangeProducts");
    console.log(res.data);
    dispatch(GET_PRODUCTS(res.data.products));
  };
  const getProduct = async (id) => {
    const res = await axios.get(`/productsDetailes/${id}`);
    console.log(res.data);
    dispatch(GET_PRODUCTS(res.data.product));
    // dispatch(GET_CATEGORIES(res.data.categories));
    // dispatch(GET_COLORS(res.data.colors));
    // dispatch(GET_SIZES(res.data.sizes));
  };
  const getCategories = async () => {
    const res = await axios.get("/dashboard/MangeCategories");
    dispatch(GET_CATEGORIES(res.data.categories));
  };
  const getSizes = async () => {
    const res = await axios.get("/dashboard/MangeSizes");
    dispatch(GET_SIZES(res.data.sizes));
  };
  const getSize = async (id) => {
    const res = await axios.get(`/dashboard/MangeSizes/${id}`);
    dispatch(GET_SIZE(res.data.size));
  };
  const getColors = async () => {
    const res = await axios.get("/dashboard/MangeColors");
    dispatch(GET_COLORS(res.data.colors));
  };
  const getAllItems = async () => {
    const res = await axios.get("/home");
    dispatch(GET_PRODUCTS(res.data.products));
    dispatch(GET_CATEGORIES(res.data.categories));
    // dispatch(GET_COLORS(res.data.colors));
    // dispatch(GET_SIZES(res.data.sizes));
    // dispatch(GET_CART(res.data.products));
  };
  const setUpdatePro = async (data) => {
    console.log(data);
    dispatch(SET_UPDATE_PRODUCT(data._id));
  };
  const setUpdateCat = async (data) => {
    console.log(data);
    dispatch(SET_UPDATE_CATEGORY(data._id));
  };
  const setUpdateSize = async (data) => {
    console.log(data);
    dispatch(SET_UPDATE_SIZE(data._id));
  };
  const setUpdateColor = async (data) => {
    console.log(data);
    dispatch(SET_UPDATE_COLOR(data._id));
  };
  const UpdateItem = async (data) => {
    try {
      if (url.includes("/MangeProducts")) {
        dispatch(UPDATE_PRODUCT(data));
        await axios.put(`/dashboard/MangeProducts/${data._id}`, data);
        getProducts();
        navigate("/dashboard/MangeProducts");
      }
      if (url.includes("/MangeCategories")) {
        dispatch(UPDATE_CATEGORY(data));
        await axios.put(`/dashboard/MangeCategories/${data._id}`, data);
        getCategories();
        navigate("/dashboard/MangeCategories");
      }
      if (url.includes("/MangeSizes")) {
        dispatch(UPDATE_SIZE(data));
        await axios.put(`/dashboard/MangeSizes/${data._id}`, data);
        getSizes();
        navigate("/dashboard/MangeSizes");
      }
      if (url.includes("/MangeColors")) {
        dispatch(UPDATE_COLOR(data));
        await axios.put(`/dashboard/MangeColors/${data._id}`, data);
        getColors();
        navigate("/dashboard/MangeColors");
      }
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  const deleteItem = async (id) => {
    try {
      if (url.includes("/MangeProducts")) {
        dispatch(DELETE_PRODUCT(id));
        await axios.delete(`/dashboard/MangeProducts/${id}`);
        getProducts();
      }
      if (url.includes("/MangeCategories")) {
        dispatch(DELETE_CATEGORY(id));
        await axios.delete(`/dashboard/MangeCategories/${id}`);
        getCategories();
      }
      if (url.includes("/MangeSizes")) {
        dispatch(DELETE_SIZE(id));
        await axios.delete(`/dashboard/MangeSizes/${id}`);
        getSizes();
      }
      if (url.includes("/MangeColors")) {
        dispatch(DELETE_COLOR(id));
        await axios.delete(`/dashboard/MangeColors/${id}`);
        getColors();
      }
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  const toCart = (item) => {
    dispatch(addToCart(item));
  };
  // const reset = () => {
  //   if (deletedPro) dispatch(RESET_PRO());
  //   if (deletedCat) dispatch(RESET_CAT());
  //   if (deletedColor) dispatch(RESET_SIZE());
  //   if (deletedSize) dispatch(RESET_SIZE());
  //   if (updatePro) dispatch(RESET_PRO());
  //   if (updateCat) dispatch(RESET_CAT());
  //   if (updateColor) dispatch(RESET_COLOR());
  //   if (updateSize) dispatch(RESET_SIZE());
  // };
  // const logoutUser = async () => {
  //   await axios.get("/logout");
  //   console.log("f");
  //   navigate("/");
  // };

  const getUser = async () => {
    try {
      const { data } = await axios.get("/getUser");
      console.log(data);
      const { user } = data;
      dispatch(SETUP_USER_SUCCESS({ user: user }));

      console.log(user);
    } catch (error) {
      if (error.response.status === 401) return;
      // logoutUser();
    }
  };
  useEffect(() => {
    getUser();
  }, []);
  // useEffect(() => {
  //   (async () => {
  //     if (x.current) {
  //       x.current = false;
  //       const res = await axios.get("/cart");
  //       console.log(res.data);
  //       // setCartItem({ cartItem, ...res.data });

  //       toCart(res.data);
  //     }
  //   })();
  // });
  const x = useRef(true);
  useEffect(
    () => {
      if (x.current) {
        x.current = false;
        if (url !== "/productsDetailes/") getAllItems();
      }
      // (async () => {
      //   const res = await axios.get("/dashboard");
      //   console.log(res.data.products);
      //   dispatch(GET_PRODUCTS(res.data.products));
      //   dispatch(GET_CATEGORIES(res.data.categories));
      //   dispatch(GET_COLORS(res.data.colors));
      //   dispatch(GET_SIZES(res.data.sizes));
      // })();
      // reset();
    },
    [
      // deletedCat,
      // deletedPro,
      // deletedColor,
      // deletedSize,
      // updatePro,
      // updateCat,
      // updateColor,
      // updateSize
    ]
  );
  return (
    <AppContext.Provider
      value={{
        displayAlert,
        successUser,
        getAllItems,
        deleteItem,
        UpdateItem,
        getCategories,
        toCart,
        getProduct,
        getSize,
        setUpdatePro,
        setUpdateColor,
        setUpdateSize,
        setUpdateCat,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};

const useAppContext = () => {
  return useContext(AppContext);
};

export { useAppContext, AppProvider };
