import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useState } from "react";
import { updateSize } from "../rtk/slices/sizesSlice";

function UpdateSize() {
  const dispatch = useDispatch();
  const params = useParams();
  const navigate = useNavigate();
  const sizes = useSelector((state) => state.sizes);
  const selectedSize = sizes.filter(
    (user) => user.id.toString() === params.id.toString()
  );
  const { sizeName, elementState } = selectedSize[0];
  const [value, setValue] = useState({
    sizeName,
    elementState,
  });
  const HandleUpdate = () => {
    dispatch(
      updateSize({
        id: params.id,
        sizeName: value.sizeName,
        elementState: value.elementState,
      })
    );
    navigate("/Dashboard/MangeSizes");
  };
  return (
    <div className="editForm">
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Size Name</Form.Label>
          <Form.Control
            type="text"
            value={value.sizeName}
            onChange={(e) => setValue({ ...value, sizeName: e.target.value })}
          />
        </Form.Group>

        <Button variant="primary" onClick={() => HandleUpdate()}>
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default UpdateSize;
