import Button from "react-bootstrap/Button";
import { useDispatch } from "react-redux";

import Form from "react-bootstrap/Form";
import { addProduct } from "../rtk/slices/productsSlice";
import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

import "./Edit.css";
function AddProduct() {
  const dispatch = useDispatch();
  const [productName, productNameSet] = useState("");
  const [category, categorySet] = useState("");
  const [price, priceSet] = useState("");
  const [img, imageSet] = useState("");
  const [product, productSet] = useState({});
  useEffect(() => {
    productSet(
      {
        id: Math.random(),
        productName,
        category,
        price,
        img,
      },
      []
    );
    navigate("/");
  });

  return (
    <div className="editForm">
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>ProductName</Form.Label>
          <Form.Control
            type="text"
            value={productName}
            onChange={(e) => productNameSet(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Category</Form.Label>
          <Form.Control
            type="text"
            value={category}
            onChange={(e) => categorySet(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Price</Form.Label>
          <Form.Control
            type="text"
            value={price}
            onChange={(e) => priceSet(e.target.value)}
          />
          target
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Image</Form.Label>
          <Form.Control
            type="file"
            value={img}
            onChange={(e) => imageSet(e.target.value)}
          />
        </Form.Group>

        <Button variant="primary" onClick={() => dispatch(addProduct(product))}>
          Add
        </Button>
      </Form>
    </div>
  );
}

export default AddProduct;
