import Button from "react-bootstrap/Button";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Alert from "react-bootstrap/Alert";
import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import CategoriesTable from "./CategoriesTable";
import ProductsTable from "./ProductsTable";
import SizesTable from "./SizesTable";
import ColorsTable from "./ColorsTable";
import Add from "../add/Add";
import DashHome from "./Dash_Home";
import axios from "axios";
import { GET_PRODUCTS } from "../../rtk/slices/productsSlice";
import { GET_CATEGORIES } from "../../rtk/slices/categoriesSlice";
import { GET_COLORS } from "../../rtk/slices/colorsSlice";
import { GET_SIZES } from "../../rtk/slices/sizesSlice";
import { useAppContext } from "../../context/appContext";

function Dashboard() {
  const { products, error, deletedPro } = useSelector(
    (state) => state.products
  );

  const { categories } = useSelector((state) => state.categories);
  const { colors } = useSelector((state) => state.colors);
  const { sizes } = useSelector((state) => state.sizes);
  const { getAllItems, deleteReset } = useAppContext();
  const dispatch = useDispatch();
  const [form, setForm] = useState(false);
  const url = window.location.pathname;
  console.log(url);
  // useEffect(() => {
  //   (async () => {
  //     try {
  //       const res = await axios.get("/dashboard");
  //       console.log(res.data.products);
  //       dispatch(GET_PRODUCTS(res.data.products));
  //       dispatch(GET_CATEGORIES(res.data.categories));
  //       dispatch(GET_COLORS(res.data.colors));
  //       dispatch(GET_SIZES(res.data.sizes));
  //       // await getAllItems();
  //     } catch (e) {
  //       console.log(e);
  //     }

  //     // deleteReset();
  //   })();
  // }, [dispatch]);

  return (
    <>
      {error && <Alert color="warning">Error in Api</Alert>}
      <Row>
        <Col md="2" className="p-0">
          <div className="control-side ">
            <div className="admin-avatar">
              <div className="logo">
                <i className="fa-solid fa-d"></i>
              </div>
              <h5>Diwan Shop</h5>
            </div>
            <div className="control-list">
              <ul>
                <Link className="nav-link" to="/dashboard">
                  <li>
                    <i className="fa-solid fa-home"></i>
                    <span> Home</span>
                  </li>
                </Link>
                <Link className="nav-link" to="/dashboard/MangeProducts">
                  <li>
                    <i className="fa-solid fa-bag-shopping"></i>
                    <span> Mange Products</span>
                  </li>
                </Link>
                <Link className="nav-link" to="/dashboard/MangeCategories">
                  <li>
                    {" "}
                    <i className="fa-solid fa-chart-simple"></i>
                    <span> Mange Categories</span>
                  </li>
                </Link>
                <Link className="nav-link" to="/dashboard/MangeSizes">
                  <li>
                    <i className="fa-solid fa-ruler-horizontal"></i>
                    <span> Mange Sizes</span>
                  </li>
                </Link>
                <Link className="nav-link" to="/dashboard/MangeColors">
                  <li>
                    <i className="fa-sharp fa-solid fa-palette"></i>{" "}
                    <span>Mange Colors</span>
                  </li>
                </Link>
              </ul>
            </div>
          </div>
        </Col>
        <Col md="10" className="home-section">
          <div className="dashboard-nav">
            <i className="fa-solid fa-bars-staggered"></i>
            {url !== "/dashboard" && (
              <Button style={{ width: "115px" }} onClick={() => setForm(true)}>
                {url.includes("/dashboard/MangeProducts") && "AddProduct"}
                {url.includes("/dashboard/MangeCategories") && "AddCategory"}
                {url.includes("/dashboard/MangeSizes") && "AddSize"}
                {url.includes("/dashboard/MangeColors") && "AddColor"}
              </Button>
            )}
          </div>
          <Add formState={form} setForm={setForm} />

          <div className="p-4">
            {url === "/dashboard" && <DashHome />}
            {url.includes("/dashboard/MangeProducts") &&
              products.length > 0 && (
                <ProductsTable formState={form} setForm={setForm} />
              )}
            {url.includes("/dashboard/MangeCategories") &&
              categories.length > 0 && (
                <CategoriesTable formState={form} setForm={setForm} />
              )}
            {url.includes("/dashboard/MangeSizes") && sizes.length > 0 && (
              <SizesTable formState={form} setForm={setForm} />
            )}
            {url.includes("/dashboard/MangeColors") && colors.length > 0 && (
              <ColorsTable formState={form} setForm={setForm} />
            )}
          </div>
        </Col>
      </Row>
    </>
  );
}
export default Dashboard;
