import Container from "react-bootstrap/Container";
import Table from "react-bootstrap/Table";
import { useDispatch, useSelector } from "react-redux";
import Button from "react-bootstrap/Button";
import { deleteCart, clearCart } from "../rtk/slices/cartSlice";
import Edit from "./Edit";
import { useState } from "react";

function Cart() {
  const [x, xset] = useState(false);
  const cartItems = useSelector((state) => state.cart);

  const dispatch = useDispatch();
  const total = cartItems.reduce(
    (previousScore, currentScore) =>
      (previousScore += currentScore.qnt * parseInt(currentScore.unitPrice)),
    0
  );

  const zz = (e) => {
    console.log(e);
    const s = cartItems.find((x) => x.id.toString() === e.toString());
    console.log(s);
    xset(e);
  };
  return (
    <>
      <Container className="p-5">
        <h1>cart</h1>
        <Button variant="primary" onClick={() => dispatch(clearCart())}>
          Clear Cart
        </Button>
        <h3>{total}</h3>
        <Table striped bordered hover className="m-3">
          <thead>
            <tr>
              <th>#</th>
              <th>First Name</th>
              <th>Units</th>
              <th>unitPrice</th>
              <th>action</th>
              <th>qnt</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((cartItem) => (
              <tr key={cartItem.id}>
                <td>{cartItem.id}</td>
                <td>{cartItem.productName}</td>
                <td>{cartItem.quantityPerUnit}</td>
                <td>{cartItem.unitPrice}</td>
                <td>{cartItem.qnt}</td>
                <td>
                  <Button
                    variant="danger"
                    onClick={() => dispatch(deleteCart(cartItem.id))}
                  >
                    Delete
                  </Button>
                  <Button
                    value={cartItem.id}
                    onClick={(e) => {
                      zz(e.target.value);
                    }}
                    variant="success"
                  >
                    Edit
                  </Button>
                  {x == cartItem.id
                    ? true
                    : false && <Edit cartItemId={cartItem.id} />}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Container>
    </>
  );
}
export default Cart;
