import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import { useAppContext } from "../../context/appContext";

const AddSizeForm = () => {
  const { setForm, addSize } = useAppContext();
  const [data, setData] = useState({
    sizeName: "",
    elementState: false,
  });

  return (
    <form onSubmit={(e) => addSize(e, data)} encType="multipart/form-data">
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>

      <div className="mb-3 text-start">
        <label>Size Name</label>
        <input
          type="text"
          className="form-control"
          value={data.sizeName}
          onChange={(e) => setData({ ...data, sizeName: e.target.value })}
        />
      </div>
      <div className="mb-3 form-check form-switch">
        <label>State</label>

        <input
          type="checkbox"
          id="custom-switch"
          label="Active"
          className="form-check-input"
          value={data.elementState}
          onChange={(e) =>
            setData({ ...data, elementState: !data.elementState })
          }
        />
      </div>

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Size
        </button>
      </div>
    </form>
  );
};

export default AddSizeForm;
