import React, { useContext, useState, useRef } from "react";
import style from "./RegisterScreen.module.css";
import authApi from "../../api/auth";
import axios from "axios";
import UserContext from "../../context/userContext";
import { useSelector } from "react-redux";
import AlertMsg from "../Alert";
import { useNavigate } from "react-router-dom";
import { useAppContext } from "../../context/appContext";
function RegisterScreen(props) {
  const navigate = useNavigate();

  // const [data, setData] = useState({
  //   fname: "",
  //   lname: "",
  //   email: "",
  //   password: "",
  // });
  const fname = useRef(null);
  const lname = useRef(null);
  const email = useRef(null);
  const password = useRef(null);
  const { showAlert, alertType, alertText } = useSelector(
    (state) => state.auth
  );
  const { user } = useContext(UserContext);
  const { displayAlert, successRegister } = useAppContext();
  const fetchData = async (e) => {
    e.preventDefault();
    // if (
    //   !data.email ||
    //   !data.password ||
    //   !data.fname ||
    //   !data.lname ||
    //   (!data.email && !data.password && !data.fname && !data.lname)
    // ) {
    //   displayAlert();
    // }
    const data = {
      fname: fname.current.value,
      lname: lname.current.value,
      email: email.current.value,
      password: password.current.value,
    };
    try {
      const res = await axios.post("/register", data);
      successRegister(user);
      authApi.setToken(res.data.token);
      setTimeout(() => {
        navigate("/login");
      }, 2000);
    } catch (e) {
      displayAlert(e.response.data.msg);
      console.log(e.response.data.msg);
    }
  };

  return (
    <>
      <form className={style.registerform} onSubmit={fetchData}>
        <h3>Sign Up</h3>
        {showAlert && <AlertMsg alertType={alertType} alertText={alertText} />}

        <div className="mb-3 text-start">
          <label>First name</label>
          <input
            type="text"
            className="form-control"
            // onChange={(e) => setData({ ...data, fname: e.target.value })}
            ref={fname}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Last name</label>
          <input
            type="text"
            className="form-control"
            // onChange={(e) => setData({ ...data, lname: e.target.value })}
            ref={lname}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Email address</label>
          <input
            type="email"
            className="form-control"
            // onChange={(e) => setData({ ...data, email: e.target.value })}
            ref={email}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            // onChange={(e) => setData({ ...data, password: e.target.value })}
            ref={password}
          />
        </div>

        <div className="d-grid">
          <button type="submit" className="btn btn-primary">
            Sign Up
          </button>
        </div>
        <p className="forgot-password text-right">
          Already registered? <a href="/login">Login</a>
        </p>
      </form>
    </>
  );
}

export default RegisterScreen;
