import Button from "react-bootstrap/Button";
import { useDispatch } from "react-redux";
import "./AddProduct.css";
import Form from "react-bootstrap/Form";
import { insert } from "../rtk/slices/productsSlice";
import { insertCategory } from "../rtk/slices/categoriesSlice";
import { insertSize } from "../rtk/slices/sizesSlice";
import { insertColor } from "../rtk/slices/colorsSlice";
import { useRef } from "react";
import { useNavigate } from "react-router-dom";
import CloseButton from "react-bootstrap/CloseButton";
import ReactDOM from "react-dom";
import React, { useContext, useState } from "react";
import axios from "axios";

import "./update/UpdateCart.css";
function AddProduct({ formState, setForm }) {
  const navigate = useNavigate();

  const dispatch = useDispatch();

  const productName = useRef(null);
  const category = useRef(null);
  const price = useRef(null);
  const img = useRef(null);
  const categoryName = useRef(null);
  const sizeName = useRef(null);
  const colorName = useRef(null);
  const elementState = useRef(false);
  // const categoryImg = useRef(null);
  const [data, setData] = useState({
    name: "",
    description: "",
    price: "",
    categories: "",
    image: "",
  });
  // const AddProduct = () => {
  //   const product = {
  //     productName: productName.current.value,
  //     category: category.current.value,
  //     price: price.current.value,
  //     img: "./img/" + img.current.value.slice(12),
  //   };

  //   dispatch(insert(product));
  //   navigate("/dashboard/MangeProducts");
  // };
  const AddCategory = () => {
    const category = {
      categoryName: categoryName.current.value,
      elementState: elementState.current.checked,

      // img: "./img/" + categoryImg.current.value.slice(12),
    };

    dispatch(insertCategory(category));
    navigate("/dashboard/MangeCategories");
  };
  const AddSize = () => {
    const size = {
      sizeName: sizeName.current.value,
      elementState: elementState.current.checked,
    };

    dispatch(insertSize(size));
    navigate("/dashboard/MangeSizes");
  };
  const AddColor = () => {
    const color = {
      colorName: colorName.current.value,
      elementState: elementState.current.checked,
    };

    dispatch(insertColor(color));
    navigate("/dashboard/MangeColors");
  };
  const Overlay = () => (
    <div className="overlay" onClick={() => setForm(false)}></div>
  );
  const BackDrop = ({ children }) => <div className="addForm">{children}</div>;

  const fetchData = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("/addProduct", data);
      console.log(res);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  return (
    formState && (
      <>
        {ReactDOM.createPortal(
          <>
            <Overlay />
            <BackDrop>
              {window.location.pathname.includes(
                "/dashboard/MangeProducts"
              ) && (
                // <Form onSubmit={fetchData}>
                //   <span className="delete-section">
                //     <CloseButton
                //       onClick={() => {
                //         setForm(false);
                //       }}
                //     />
                //   </span>
                //   <Form.Group className="mb-3" controlId="formBasicEmail">
                //     <Form.Label>Product Name</Form.Label>
                //     <Form.Control
                //       type="text"
                //       onChange={(e) =>
                //         setData({ ...data, name: e.target.value })
                //       }
                //     />
                //   </Form.Group>
                //   <Form.Group className="mb-3" controlId="formBasicEmail">
                //     <Form.Label>Category</Form.Label>
                //     <Form.Control
                //       type="text"
                //       onChange={(e) =>
                //         setData({ ...data, categories: e.target.value })
                //       }
                //     />
                //   </Form.Group>
                //   <Form.Group className="mb-3" controlId="formBasicEmail">
                //     <Form.Label>description</Form.Label>
                //     <Form.Control
                //       type="text"
                //       onChange={(e) =>
                //         setData({ ...data, categories: e.target.value })
                //       }
                //     />
                //   </Form.Group>
                //   <Form.Group className="mb-3" controlId="formBasicEmail">
                //     <Form.Label>Price</Form.Label>
                //     <Form.Control
                //       type="text"
                //       onChange={(e) =>
                //         setData({ ...data, price: e.target.value })
                //       }
                //     />
                //     target
                //   </Form.Group>
                //   <Form.Group className="mb-3" controlId="formBasicEmail">
                //     <Form.Label>Image</Form.Label>
                //     <Form.Control
                //       type="file"
                //       onChange={(e) =>
                //         setData({ ...data, image: e.target.value })
                //       }
                //     />
                //   </Form.Group>

                //   <Button
                //     variant="primary"
                //     onClick={() => {
                //       AddProduct();
                //       setForm(false);
                //     }}
                //   >
                //     Add
                //   </Button>
                // </Form>
                <div className="mb-3 text-start">
                  <label>First name</label>
                  <input
                    type="text"
                    className="form-control"
                    onChange={(e) => setData({ ...data, name: e.target.value })}
                  />
                </div>
              )}
              {window.location.pathname.includes(
                "/dashboard/MangeCategories"
              ) && (
                <Form onSubmit={fetchData}>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Category Name</Form.Label>
                    <Form.Control type="text" ref={categoryName} />
                  </Form.Group>
                  <Form.Check
                    type="switch"
                    id="custom-switch"
                    label="Active"
                    ref={elementState}
                  />
                  <Button
                    variant="primary"
                    onClick={() => {
                      AddCategory();
                      setForm(false);
                    }}
                  >
                    Add
                  </Button>
                </Form>
              )}
              {window.location.pathname.includes("/dashboard/MangeSizes") && (
                <Form onSubmit={fetchData}>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Size Name</Form.Label>
                    <Form.Control type="text" ref={sizeName} />
                  </Form.Group>

                  <Button
                    variant="primary"
                    onClick={() => {
                      AddSize();
                      setForm(false);
                    }}
                  >
                    Add
                  </Button>
                </Form>
              )}
              {window.location.pathname.includes("/dashboard/MangeColors") && (
                <Form>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Color Name</Form.Label>
                    <Form.Control type="text" ref={colorName} />
                  </Form.Group>

                  <Button
                    variant="primary"
                    onClick={() => {
                      AddColor();
                      setForm(false);
                    }}
                  >
                    Add
                  </Button>
                </Form>
              )}
            </BackDrop>
          </>,
          document.getElementById("modal")
        )}
      </>
    )
  );
}

export default AddProduct;
