import Button from "react-bootstrap/Button";

import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { deleteCat } from "../../rtk/slices/categoriesSlice";
import ListGroup from "react-bootstrap/ListGroup";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import CloseButton from "react-bootstrap/CloseButton";
import AddProduct from "../AddProduct";
import Form from "react-bootstrap/Form";

const CategoriesTable = ({ formState, setForm }) => {
  const dispatch = useDispatch();
  const categories = useSelector((state) => state.categories);

  return (
    <>
      <Table>
        <thead>
          <tr>
            <th>#</th>
            <th>Category Name</th>
            <th>State</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category) => (
            <tr key={category.id}>
              <td style={{ width: " 20px" }}>{category.id}</td>
              <td>{category.categoryName}</td>
              <td>
                {" "}
                <span className="delete-section">
                  <CloseButton
                    onClick={() => {
                      dispatch(deleteCat(category));
                    }}
                  />
                </span>
                <span className="edit-section">
                  <Link to={`/Dashboard/MangeCategories/Update/${category.id}`}>
                    <i className="fa-regular fa-pen-to-square"></i>
                  </Link>
                </span>
              </td>
              <td></td>
              <td>
                {" "}
                <Form>
                  <Form.Check
                    type="switch"
                    id="custom-switch"
                    label="Check this switch"
                  />
                  <Form.Check
                    disabled
                    type="switch"
                    label="disabled switch"
                    id="disabled-custom-switch"
                  />
                </Form>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <AddProduct formState={formState} setForm={setForm} />
    </>
  );
};

export default CategoriesTable;
