import React, { useContext, useState, useRef } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import axios from "axios";
import { useNavigate } from "react-router-dom";
// import AlertMsg from "../Alert";
import { ADD_PRODUCT } from "../../rtk/slices/productsSlice";
import { useDispatch, useSelector } from "react-redux";
import Form from "react-bootstrap/Form";

const AddProductForm = ({ setForm }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { sizes } = useSelector((state) => state.sizes);

  const [data, setData] = useState({
    name: "",
    description: "",
    categories: "",
    price: "",
    colorList: "",
    sizeList: "",
    // elementState: "",
    image: [],
  });
  console.log(data);
  const handleImage = (e) => {
    const x = e.target.files;
    console.log(x);
    const list = [];

    for (let i = 0; i < x.length; i++) {
      list.push(e.target.files[i].name);
    }
    console.log(list);

    setData({ ...data, image: list });
  };
  const setSize = (e, d) => {
    console.log(e);
    console.log(d);
    let list = [];
    if (d) {
      list.push(e);
    }
  };
  const fetchData = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("/dashboard/MangeProducts/addProduct", data);
      console.log(res.data.product);
      dispatch(ADD_PRODUCT([res.data.product]));
      setForm(false);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  return (
    <form onSubmit={fetchData} encType="multipart/form-data">
      {/* {showAlert && <AlertMsg alertType={alertType} alertText={alertText} />} */}
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>

      <div className="mb-3 text-start">
        <label>Name</label>
        <input
          type="text"
          className="form-control"
          value={data.fname}
          onChange={(e) => setData({ ...data, name: e.target.value })}
        />
      </div>
      <div className="mb-3 text-start">
        <label>Description</label>
        <input
          type="text"
          className="form-control"
          value={data.description}
          onChange={(e) => setData({ ...data, description: e.target.value })}
        />
      </div>
      <div className="mb-3 text-start">
        <label>Categories</label>
        <input
          type="text"
          className="form-control"
          value={data.categories}
          onChange={(e) => setData({ ...data, categories: e.target.value })}
        />
      </div>
      <div className="mb-3 text-start">
        <label>Price</label>
        <input
          type="text"
          className="form-control"
          value={data.price}
          onChange={(e) => setData({ ...data, price: e.target.value })}
        />
      </div>
      <div className="row">
        <div className="col-md-6">
          {" "}
          <h5>Sizes</h5>
          {sizes.map((size) => (
            <div className="form-check form-switch">
              <label>{size.sizeName}</label>
              <input
                className="form-check-input"
                type="checkbox"
                id="custom-switch"
                name={size.sizeName}
                // checked={data.sizeList === true ? true : false}
                onChange={(e) => {
                  setSize(e.target.name, e.target.checked);
                }}
              />
            </div>
          ))}
        </div>
        <div className="col-md-6">
          {" "}
          <h5>Colors</h5>
          <div className="form-check form-switch">
            <label>White</label>
            <input
              className="form-check-input"
              type="checkbox"
              id="custom-switch"
            />
          </div>
        </div>
      </div>
      {/* <div className="form-check form-switch">
        <label>State</label>
        <input
          className="form-check-input"
          type="checkbox"
          id="custom-switch"
          checked={data.elementState === true ? true : false}
          onChange={(e) =>
            setData({ ...data, elementState: !data.elementState })
          }
        />
      </div> */}
      <div className="mb-3 text-start">
        <label>Image</label>
        <input
          multiple
          type="file"
          name="image"
          className="form-control"
          onChange={(e) => handleImage(e)}
        />
      </div>
      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Product
        </button>
      </div>
    </form>
  );
};

export default AddProductForm;
