import "./App.css";
import React, { useState, useEffect } from "react";
// import AppNavbar from "./component/AppNavbar";
import { Route, Routes } from "react-router-dom";
import Home from "./component/Home";
import Dashboard from "./component/Dashboard/Dashboard";
import Cart from "./component/Cart";
import UpdateProduct from "./component/update/UpdateProduct";
import UpdateCategory from "./component/update/UpdateCategory";
import UpdateSize from "./component/update/UpdateSize";
import UpdateColor from "./component/update/UpdateColor";
import RegisterScreen from "./component/registerScreen/RegisterScreen";
import LoginScreen from "./component/registerScreen/LoginScreen";
import authApi from "./api/auth";
import UserContext from "./context/appContext";
import AppContext from "./context/context";
import Edit from "./component/update/UpdateCart";
import { useDispatch, useSelector } from "react-redux";
import { CLEAR_ALERT } from "../rtk/slices/authSlice";

function App() {
  const dispatch = useDispatch();
  const clearAlert = () => {
    setTimeout(() => dispatch(CLEAR_ALERT()), 2500);
  };

  const [user, setUser] = useState(null);
  useEffect(() => {
    setUser(authApi.getUser() || null);
  }, []);
  return (
    <UserContext.Provider value={{ user, setUser }}>
      <AppContext.Provider value={{ clearAlert }}>
        <div className="App">
          {window.location.pathname.includes("/dashboard") && (
            <Routes>
              <Route path="/dashboard" element={<Dashboard />}></Route>
              <Route
                path="/dashboard/MangeProducts"
                element={<Dashboard />}
              ></Route>
              <Route
                path="/dashboard/MangeCategories"
                element={<Dashboard />}
              ></Route>
              <Route
                path="/dashboard/MangeSizes"
                element={<Dashboard />}
              ></Route>
              <Route
                path="/dashboard/MangeColors"
                element={<Dashboard />}
              ></Route>
              <Route
                path="/dashboard/MangeProducts/Delete/:id"
                element={<Dashboard />}
              ></Route>
              <Route
                path="/dashboard/MangeCategories/Delete/:id"
                element={<Dashboard />}
              ></Route>
              <Route
                path="/dashboard/MangeSizes/Delete/:id"
                element={<Dashboard />}
              ></Route>
              <Route
                path="/dashboard/MangeColors/Delete/:id"
                element={<Dashboard />}
              ></Route>

              <Route
                path="/dashboard/MangeProducts/Update/:id"
                element={<UpdateProduct />}
              ></Route>
              <Route
                path="/dashboard/MangeCategories/Update/:id"
                element={<UpdateCategory />}
              ></Route>
              <Route
                path="/dashboard/MangeCategories/UpdateState/:id"
                element={<Dashboard />}
              ></Route>
              <Route
                path="/dashboard/MangeSizes/Update/:id"
                element={<UpdateSize />}
              ></Route>
              <Route
                path="/dashboard/MangeSizes/UpdateState/:id"
                element={<Dashboard />}
              ></Route>
              <Route
                path="/dashboard/MangeColors/Update/:id"
                element={<UpdateColor />}
              ></Route>
              <Route
                path="/dashboard/MangeColors/UpdateState/:id"
                element={<Dashboard />}
              ></Route>
            </Routes>
          )}
          {window.location.pathname.includes("/register") && (
            <Routes>
              <Route path="/register" element={<RegisterScreen />}></Route>
            </Routes>
          )}
          {window.location.pathname.includes("/login") && (
            <Routes>
              <Route path="/login" element={<LoginScreen />}></Route>
            </Routes>
          )}
          <>
            {/* <AppNavbar /> */}
            <Routes>
              <Route path="/" element={<Home />}></Route>
              <Route path="/cart" element={<Cart />}></Route>
              <Route path="/edit/:id" element={<Edit />}></Route>
              <Route path="/register" element={<RegisterScreen />}></Route>
              <Route path="/login" element={<LoginScreen />}></Route>
            </Routes>
          </>
        </div>
      </AppContext.Provider>
    </UserContext.Provider>
  );
}

export default App;
