import Button from "react-bootstrap/Button";

import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { deletePro, api } from "../../rtk/slices/productsSlice";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import { useEffect } from "react";

const ProductsTable = () => {
  const { products, error } = useSelector((state) => state.products);
  const dispatch = useDispatch();
  const [filterVal, setFilter] = useState("");
  useEffect(() => {
    dispatch(api());
  }, [dispatch]);

  const productsFilter = () => {
    const e = products.filter((e) => e.productName.includes(filterVal));
    return e;
  };
  return (
    <Table striped>
      <thead>
        <tr>
          <th>Photo</th>
          <th>First Name</th>
          <th>Category</th>
          <th>Price</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {productsFilter().map((product) => (
          <tr key={product.id}>
            <td>
              <img
                src={product.img}
                style={{ width: "50px", height: "50px" }}
                alt="Not Found"
              />
            </td>
            <td>{product.productName}</td>
            <td>{product.category}</td>
            <td>{product.price}</td>
            <td>
              <Link to={`/Dashboard/MangeProducts`}>
                <Button
                  className="m-2"
                  variant="danger"
                  onClick={() => {
                    dispatch(deletePro(product));
                  }}
                >
                  Delete
                </Button>
              </Link>

              <Link to={`/Update/${product.id}`}>
                <Button value={product.id} variant="success">
                  Edit
                </Button>
              </Link>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default ProductsTable;
