import { useDispatch } from "react-redux";
import React, { useContext } from "react";
import { CLEAR_ALERT, DISPLAY_ALERT } from "../rtk/slices/authSlice";
const AppContext = React.createContext();

const AppProvider = ({ children }) => {
  const dispatch = useDispatch();

  const clearAlert = () => {
    setTimeout(() => dispatch(CLEAR_ALERT()), 2500);
  };

  const displayAlert = (type, msg) => {
    dispatch(DISPLAY_ALERT({ alertText: msg }));
    clearAlert();
  };
  const successRegister = () => {
    dispatch(REGISTER_SUCCESS({ user: user }));
    setTimeout(() => {
      navigate("/login");
    }, 2000);
  };

  return (
    <AppContext.Provider value={{ displayAlert }}>
      {children}
    </AppContext.Provider>
  );
};

const useAppContext = () => {
  return useContext(AppContext);
};

export { useAppContext, AppProvider };
