import { useDispatch, useSelector } from "react-redux";
import React, { useContext } from "react";
import { useEffect, useState } from "react";
import { DELETE_PRODUCT, DELETE_RESET } from ".././rtk/slices/productsSlice";
import {
  DELETE_CATEGORY,
  DELETE_RESET_CAT,
} from ".././rtk/slices/categoriesSlice";

import {
  CLEAR_ALERT,
  DISPLAY_ALERT,
  REGISTER_SUCCESS,
} from "../rtk/slices/authSlice";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { GET_PRODUCTS } from ".././rtk/slices/productsSlice";
import { GET_CATEGORIES } from ".././rtk/slices/categoriesSlice";
import { GET_COLORS } from ".././rtk/slices/colorsSlice";
import { GET_SIZES } from ".././rtk/slices/sizesSlice";

const AppContext = React.createContext();

const AppProvider = ({ children }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { deletedPro } = useSelector((state) => state.products);

  const { deletedCat } = useSelector((state) => state.categories);
  const { colors } = useSelector((state) => state.colors);
  const { sizes } = useSelector((state) => state.sizes);
  const clearAlert = () => {
    setTimeout(() => dispatch(CLEAR_ALERT()), 2000);
  };

  const displayAlert = (msg) => {
    dispatch(DISPLAY_ALERT({ alertText: msg }));
    clearAlert();
  };
  const successRegister = (user) => {
    dispatch(REGISTER_SUCCESS({ user: user }));
    clearAlert();

    setTimeout(() => {
      navigate("/login");
    }, 2000);
  };

  const getAllItems = async () => {
    // const res = await axios.get("/dashboard");
    // console.log(res.data.products);
    // dispatch(GET_PRODUCTS(res.data.products));
    // dispatch(GET_CATEGORIES(res.data.categories));
    // dispatch(GET_COLORS(res.data.colors));
    // dispatch(GET_SIZES(res.data.sizes));
  };

  const deleteItem = async (id) => {
    const url = window.location.pathname;
    if (url.includes("/MangeProducts")) {
      await axios.delete(`/dashboard/MangeProducts/Delete/${id}`);
      dispatch(DELETE_PRODUCT(id));
    }
    if (url.includes("/MangeCategories")) {
      await axios.delete(`/dashboard/MangeCategories/Delete/${id}`);
      console.log("fd");
      dispatch(DELETE_CATEGORY(id));
    }
  };
  // const deleteReset = async () => {
  //   if (deletedPro) dispatch(DELETE_RESET());
  //   if (deletedCat) dispatch(DELETE_RESET_CAT());
  // };
  useEffect(() => {
    (async () => {
      const res = await axios.get("/dashboard");
      console.log(res.data.products);
      dispatch(GET_PRODUCTS(res.data.products));
      dispatch(GET_CATEGORIES(res.data.categories));
      dispatch(GET_COLORS(res.data.colors));
      dispatch(GET_SIZES(res.data.sizes));
    })();

    if (deletedPro) dispatch(DELETE_RESET());
    if (deletedCat) dispatch(DELETE_RESET_CAT());
  }, [dispatch, deletedCat, deletedPro]);
  return (
    <AppContext.Provider
      value={{
        displayAlert,
        successRegister,
        getAllItems,
        deleteItem,
        // deleteReset,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};

const useAppContext = () => {
  return useContext(AppContext);
};

export { useAppContext, AppProvider };
