import Button from "react-bootstrap/Button";

import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { deleteSize } from "../../rtk/slices/sizesSlice";
import ListGroup from "react-bootstrap/ListGroup";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import CloseButton from "react-bootstrap/CloseButton";
import AddProduct from "../add/Add";
import Form from "react-bootstrap/Form";
import { updateSize } from "../../rtk/slices/sizesSlice";
import { useNavigate } from "react-router-dom";

const SizesTable = ({ formState, setForm }) => {
  const dispatch = useDispatch();
  const { sizes } = useSelector((state) => state.sizes);
  const navigate = useNavigate();

  const HandleUpdate = (e) => {
    dispatch(
      updateSize({
        id: e.id,
        sizeName: e.name,
        elementState: e.checked,
      })
    );
    navigate("/dashboard/MangeSizes");
  };
  return (
    <>
      <Table>
        <thead>
          <tr>
            {/* <th>#</th> */}
            <th>SizeName</th>
            <th>State</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {sizes.map((size) => (
            <tr key={size._id}>
              {/* <td style={{ width: " 20px" }}>{size._id}</td> */}
              <td>{size.sizeName}</td>
              <td>
                {" "}
                <Form>
                  <Link to={`/dashboard/MangeSizes/UpdateState/${size._id}`}>
                    <Form.Check
                      type="switch"
                      id={size._id}
                      label="Active"
                      name={size.sizeName}
                      checked={size.elementState === true ? true : false}
                      onChange={(e) => HandleUpdate(e.target)}
                    />
                  </Link>
                </Form>
              </td>

              <td>
                {" "}
                <span className="delete-section">
                  <CloseButton
                    onClick={() => {
                      dispatch(deleteSize(size));
                    }}
                  />
                </span>
                <span className="edit-section">
                  <Link to={`/dashboard/MangeSizes/Update/${size.id}`}>
                    <i className="fa-regular fa-pen-to-square"></i>
                  </Link>
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <AddProduct formState={formState} setForm={setForm} />
    </>
  );
};

export default SizesTable;
