import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
export const apiCategories = createAsyncThunk(
  "categoriesSlice/apiCategories",
  async () => {
    const res = await fetch("http://localhost:3000/categories");
    const data = await res.json();
    return data;
  }
);

export const insertCategory = createAsyncThunk(
  "categoriesSlice/insertCategory",
  async (categoryName, thunkApi) => {
    const { rejectWithValue } = thunkApi;
    try {
      const res = await fetch("http://localhost:3000/categories", {
        method: "POST",

        body: JSON.stringify(categoryName),

        headers: { "Content-type": "application/json; charset=utf-8" },
      });

      const data = await res.json();
      return data;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);
export const update = createAsyncThunk(
  "categoriesSlice/update",
  async (categoryData, thunkApi) => {
    const { rejectWithValue } = thunkApi;
    try {
      const res = await fetch(
        `http://localhost:3000/categories/${categoryData.id}`,
        {
          method: "PUT",

          body: JSON.stringify(categoryData),

          headers: { "Content-type": "application/json; charset=utf-8" },
        }
      );
      const data = await res.json();
      return data;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);
export const deleteCat = createAsyncThunk(
  "categoriesSlice/deleteCat",
  async (categoryData, thunkApi) => {
    const { rejectWithValue } = thunkApi;
    console.log(categoryData);
    try {
      await fetch(`http://localhost:3000/categories/${categoryData.id}`, {
        method: "DELETE",

        headers: { "Content-type": "application/json; charset=utf-8" },
      });
      return categoryData;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);
const categoriesSlice = createSlice({
  name: "categoriesSlice",
  initialState: [],
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(apiCategories.fulfilled, (state, action) => {
      return action.payload;
    });
    builder.addCase(insertCategory.fulfilled, (state, action) => {
      state.push(action.payload);
    });
    builder.addCase(deleteCat.fulfilled, (state, action) => {
      return (state = state.filter(
        (category) => category.id !== action.payload.id
      ));
    });

    builder.addCase(update.fulfilled, (state, action) => {
      const { id, productName, price, category, img } = action.payload;
      const updateState = state.products.find((product) => product.id === id);
      console.log(action.payload.img);
      if (updateState) {
        updateState.productName = productName;
        updateState.price = price;
        updateState.category = category;
        updateState.img = img;
      }
    });
  },
});

// export const {} = categoriesSlice.actions;
export default categoriesSlice.reducer;
