import React, { useState, useContext } from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import AlertMsg from "../Alert";
import axios from "axios";
import authApi from "../../api/auth";
import UserContext from "../../context/userContext";
import { useAppContext } from "../../context/appContext";

function LoginScreen(props) {
  const { user } = useContext(UserContext);
  const { displayAlert, successRegister } = useAppContext();

  const { showAlert, alertType, alertText } = useSelector(
    (state) => state.auth
  );
  const navigate = useNavigate();

  const [data, setData] = useState({
    email: "",
    password: "",
  });
  const fetchData = async (e) => {
    e.preventDefault();
    // if (user) {
    //   dispatch(SETUP_USER_SUCCESS({ user: user }));
    // }
    // if (!data.email || !data.password || (!data.email && !data.password)) {
    //   // dispatch(DISPLAY_ALERT());
    //   displayAlert();
    // }
    try {
      const res = await axios.post("/login", data);
      successRegister(user);
      authApi.setToken(res.data);
      setTimeout(() => {
        navigate("/");
      }, 3000);
    } catch (e) {
      // dispatch(
      //   DISPLAY_ALERT({ alertType: "danger", alertText: e.response.data.msg })
      // );
      displayAlert(e.response.data.msg);
      console.log(e.response.data.msg);
    }
  };

  return (
    <>
      <form className="registerform " onSubmit={fetchData}>
        <h3>Log In</h3>
        {showAlert && <AlertMsg alertType={alertType} alertText={alertText} />}
        <div className="mb-3 text-start">
          <label>Email address</label>
          <input
            type="email"
            className="form-control"
            onChange={(e) => setData({ ...data, email: e.target.value })}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            onChange={(e) => setData({ ...data, password: e.target.value })}
          />
        </div>

        <div className="d-grid">
          <button type="submit" className="btn btn-primary">
            Log In
          </button>
        </div>
        <p className="forgot-password text-right">
          <a href="/register">Not a member yet?</a>
        </p>
      </form>
    </>
  );
}

export default LoginScreen;
