import Button from "react-bootstrap/Button";

import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { deletePro } from "../../rtk/slices/productsSlice";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import CloseButton from "react-bootstrap/CloseButton";
const ProductsTable = () => {
  const { products } = useSelector((state) => state.products);
  const dispatch = useDispatch();
  // const [filterVal, setFilter] = useState("");

  // const productsFilter = () => {
  //   const e = products.filter((e) => e.productName.includes(filterVal));
  //   return e;
  // };
  return (
    <Row>
      {products.map((product) => (
        <Col className=" col-md-3 " key={product.id}>
          <Card className="col-style ">
            <span className="delete-section">
              <CloseButton
                onClick={() => {
                  dispatch(deletePro(product));
                }}
              />
            </span>
            <span className="edit-section">
              <i class="fa-regular fa-pen-to-square"></i>
            </span>
            <Row className="d-flex align-items-center">
              <Col md={4}>
                <Card.Img variant="top" src={product.img} />
              </Col>
              <Col md={8}>
                <Card.Title>{product.productName}</Card.Title>
              </Col>
            </Row>
            <Row>
              <Col>
                <Card.Body className="mt-3">
                  <Card.Text>
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </Card.Text>
                </Card.Body>
              </Col>
            </Row>
          </Card>
        </Col>
      ))}
    </Row>
  );
};

export default ProductsTable;
