import React, { useContext, useState, useRef } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import axios from "axios";
import { useNavigate } from "react-router-dom";
// import AlertMsg from "../Alert";
import { ADD_PRODUCT } from "../../rtk/slices/productsSlice";
import { useDispatch, useSelector } from "react-redux";
import Form from "react-bootstrap/Form";

const AddProductForm = ({ setForm }) => {
  const dispatch = useDispatch();
  const { sizes } = useSelector((state) => state.sizes);
  const { colors } = useSelector((state) => state.colors);
  const { categories } = useSelector((state) => state.categories);

  const [data, setData] = useState({
    name: "",
    description: "",
    categories: "",
    price: "",
    colorList: [],
    sizeList: [],
    elementState: "",
    image: [],
  });
  const handleImage = (e) => {
    const x = e.target.files;
    const list = [];

    for (let i = 0; i < x.length; i++) {
      list.push(e.target.files[i].name);
    }

    setData({ ...data, image: list });
  };
  const setSize = (itemName, state) => {
    let list = [];
    if (state) {
      list = data.sizeList;
      list.push(itemName);
      setData({ ...data, sizeList: list });
    } else {
      list = data.sizeList.filter((item) => itemName !== item);
      setData({ ...data, sizeList: list });
    }
  };
  const setColor = (itemName, state) => {
    let list = [];
    if (state) {
      list = data.colorList;
      list.push(itemName);
      setData({ ...data, colorList: list });
    } else {
      list = data.colorList.filter((item) => itemName !== item);
      setData({ ...data, colorList: list });
    }
  };
  const fetchData = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("/dashboard/MangeProducts/addProduct", data);
      console.log(res.data.product);
      dispatch(ADD_PRODUCT([res.data.product]));
      setForm(false);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  console.log(data);
  return (
    <form onSubmit={fetchData} encType="multipart/form-data">
      {/* {showAlert && <AlertMsg alertType={alertType} alertText={alertText} />} */}
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>

      <div className="mb-3 text-start">
        <label>Name</label>
        <input
          type="text"
          className="form-control"
          value={data.fname}
          onChange={(e) => setData({ ...data, name: e.target.value })}
        />
      </div>
      <div className="mb-3 text-start">
        <label>Description</label>
        <input
          type="text"
          className="form-control"
          value={data.description}
          onChange={(e) => setData({ ...data, description: e.target.value })}
        />
      </div>
      <div className="mb-3 text-start">
        <select className="input-select m-2">
          {categories.map((category) => (
            <option
              value={category._id}
              onClick={(e) => setData({ ...data, categories: e.target.value })}
            >
              {category.categoryName}
            </option>
          ))}
        </select>
        {/* <label>Categories</label>
        <input
          type="select"
          className="form-control"
          value={data.categories}
          onChange={(e) => setData({ ...data, categories: e.target.value })}
        /> */}
      </div>
      <div className="mb-3 text-start">
        <label>Price</label>
        <input
          type="text"
          className="form-control"
          value={data.price}
          onChange={(e) => setData({ ...data, price: e.target.value })}
        />
      </div>
      <div className="row">
        <div className="col-md-6">
          {" "}
          <h5>Sizes</h5>
          {sizes.map((size) => (
            <div className="form-check form-switch">
              <label>{size.sizeName}</label>
              <input
                className="form-check-input"
                type="checkbox"
                id="custom-switch"
                name={size.sizeName}
                onChange={(e) => {
                  setSize(e.target.name, e.target.checked);
                }}
              />
            </div>
          ))}
        </div>
        <div className="col-md-6">
          {" "}
          <h5>Colors</h5>
          {colors.map((color) => (
            <div className="form-check form-switch">
              <label>{color.colorName}</label>
              <input
                className="form-check-input"
                type="checkbox"
                id="custom-switch"
                name={color.colorName}
                onChange={(e) => {
                  setColor(e.target.name, e.target.checked);
                }}
              />
            </div>
          ))}
        </div>
      </div>
      <h5>State</h5>
      <div className="mb-3 form-check form-switch">
        <label>Active</label>
        <input
          type="checkbox"
          id="custom-switch"
          label="Active"
          className="form-check-input"
          value={data.elementState}
          onChange={(e) =>
            setData({ ...data, elementState: !data.elementState })
          }
        />
      </div>

      <div className="mb-3 text-start">
        <label>Image</label>
        <input
          multiple
          type="file"
          name="image"
          className="form-control"
          onChange={(e) => handleImage(e)}
        />
      </div>
      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Product
        </button>
      </div>
    </form>
  );
};

export default AddProductForm;
