import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useState } from "react";
import { updateCat } from "../../rtk/slices/categoriesSlice";
import axios from "axios";

function UpdateCat() {
  const dispatch = useDispatch();
  const params = useParams();
  const navigate = useNavigate();
  const { categories } = useSelector((state) => state.categories);
  const selectedCategory = categories.filter((user) => user._id === params.id);
  console.log(selectedCategory[0]);
  const { categoryName, elementState } = selectedCategory[0];
  const [value, setValue] = useState({
    categoryName,
    // img,
    elementState,
    id: params.id,
  });
  console.log(value.elementState);
  const HandleUpdate = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post(
        `/dashboard/MangeCategories/Update/${value.id}`,
        value
      );

      navigate("/dashboard/MangeCategories");
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  // const HandleUpdate = () => {
  //   dispatch(
  //     updateCat({
  //       id: params.id,
  //       categoryName: value.categoryName,
  //       // img: "./img/" + value.img.slice(12),
  //       elementState: value.elementState,
  //     })
  //   );
  //   navigate("/dashboard/MangeCategories");
  // };
  return (
    <div className="editForm">
      <Form onSubmit={HandleUpdate}>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Category Name</Form.Label>
          <Form.Control
            type="text"
            value={value.categoryName}
            onChange={(e) =>
              setValue({ ...value, categoryName: e.target.value })
            }
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Image</Form.Label>
          <Form.Control
            type="file"
            onChange={(e) => setValue({ ...value, img: e.target.value })}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Image</Form.Label>
          <Form.Check
            type="switch"
            id="custom-switch"
            label="Active"
            checked={value.elementState === true ? true : false}
            onChange={(e) =>
              setValue({ ...value, elementState: !value.elementState })
            }
          />
        </Form.Group>
        <Button type="submit" variant="primary">
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default UpdateCat;
