import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import { useAppContext } from "../../context/appContext";
import FormRow from "../modal/FormRow";
import FormRowSelect from "../modal/FormRowSelect";
const AddCategoryForm = () => {
  const { setForm, addCategory } = useAppContext();

  const [data, setData] = useState({
    categoryName: "",
    image: [],
    elementState: false,
  });
  const handleImage = (e) => {
    const image = e.target.files[0].name;

    setData({ ...data, image: image });
  };

  return (
    <form
      className="editForm"
      onSubmit={(e) => addCategory(e, data)}
      encType="multipart/form-data"
    >
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>
      <FormRow
        type="text"
        name="Category Name"
        value={data.categoryName}
        handleChange={(e) => setData({ ...data, categoryName: e.target.value })}
      />

      <FormRow
        type="file"
        name="Category Name"
        handleChange={(e) => handleImage(e)}
      />

      <div className="mb-3 form-check form-switch">
        <label>State</label>

        <input
          type="checkbox"
          id="custom-switch"
          label="Active"
          className="form-check-input"
          value={data.elementState}
          onChange={(e) =>
            setData({ ...data, elementState: !data.elementState })
          }
        />
      </div>

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Category
        </button>
      </div>
    </form>
  );
};

export default AddCategoryForm;
