import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useContext } from "react";
// import { api } from "../rtk/slices/productsSlice";
import { addToCart } from "../rtk/slices/cartSlice";
import AppNavbar from "./secrions/AppNavbar";
import styled from "styled-components";

import "./Home.css";
function Home() {
  const { products } = useSelector((state) => state.products);
  const dispatch = useDispatch();
  // useEffect(() => {
  //   // dispatch(api());
  // }, [dispatch]);

  return (
    <>
      {/* <Categories />
      <Slider /> */}
      <AppNavbar />
      <Container className="mt-5 pt-5">
        <h1 className="pb-5">Products</h1>

        <Row className="px-xl-5 pb-3">
          {products.map((product) => (
            <Col
              key={product.id}
              className="col-lg-3 col-md-6 col-sm-12 p-0 product-style"
            >
              <Card className="card-style">
                <Card.Img
                  variant="top"
                  src={`/img/${product.image}`}
                  style={{ height: "100%", width: "100%" }}
                />
                <Card.Body className="card-title">
                  <Card.Text className="text-start">
                    <h5>{product.name}</h5>
                  </Card.Text>
                  <Card.Text className="text-start">
                    <p>{product.price} $</p>
                  </Card.Text>

                  <Card.Footer>
                    <button
                      className="add-button"
                      variant="dark"
                      onClick={() => dispatch(addToCart(product))}
                    >
                      <i className="fas fa-eye  mr-1"></i> View Detail
                    </button>
                    <button
                      className="add-button"
                      variant="dark"
                      onClick={() => dispatch(addToCart(product))}
                    >
                      <i className="fa-solid fa-cart-shopping"></i> Add TO Cart
                    </button>
                  </Card.Footer>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </>
  );
}

const Wrapper = styled.article`
  body {
    font-family: serif;
    background-color: white;
  }
  .product-style {
    border-radius: 4px;
    margin-bottom: 50px;
  }
  .card-style {
    width: 100%;
    /* height: 330px; */
    border: none;
  }
  .card-body {
    height: 275px;
    padding: 0;
    border: 1px solid #e5e4e4;
  }
  .card-footer {
    background-color: white;
    display: flex;
    padding: 10px 0 10px 0;
    justify-content: space-between;
  }
  .card-title {
    height: auto;
    background-color: white;
    margin: 0;
  }
  .card-text {
    padding: 10px;
  }
  .add-button {
    margin: 13px;
    background-color: #ffffff;
    border: none;
    color: #10afb4;
    font-size: 16px;
    margin: 10px;
    padding: 8px 10px;
    border-radius: 4px;
    font-weight: bolder;
  }
  /* .add-button i {
  color: #e9e9e9;
} */
`;
export default Home;
