import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useState } from "react";
import { update } from "../rtk/slices/categoriesSlice";

function UpdateCat() {
  const dispatch = useDispatch();
  const params = useParams();
  const navigate = useNavigate();
  console.log(params.id);
  const { categories } = useSelector((state) => state.categories);
  const selectedCategory = categories.filter(
    (user) => user.id.toString() === params.id.toString()
  );
  const { categoryName, img } = selectedCategory[0];
  const [value, setValue] = useState({
    categoryName,
    img,
  });
  const HandleUpdate = () => {
    dispatch(
      update({
        id: params.id,
        categoryName: value.categoryName,
        img: "./img/" + value.img.slice(12),
      })
    );
    navigate("/Dashboard/MangeCategories");
  };
  return (
    <div className="editForm">
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>ProductName</Form.Label>
          <Form.Control
            type="text"
            value={value.categoryName}
            onChange={(e) =>
              setValue({ ...value, categoryName: e.target.value })
            }
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>unitPrice</Form.Label>
          <Form.Control
            type="file"
            onChange={(e) => setValue({ ...value, img: e.target.value })}
          />
        </Form.Group>

        <Button variant="primary" onClick={() => HandleUpdate()}>
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default UpdateCat;
