import Wrapper from "../../Wrapper/Dashboard";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Card from "react-bootstrap/Card";
import AddProduct from "../add/Add";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import CloseButton from "react-bootstrap/CloseButton";
import { useAppContext } from "../../context/appContext";
import UpdateProduct from "../update/UpdateProduct";

const ProductsTable = ({ formState, setForm }) => {
  const { deleteItem, setUpdatePro } = useAppContext();
  const { products, updateProId } = useSelector((state) => state.products);
  const deleteProduct = (id) => {
    deleteItem(id);
  };

  return (
    <>
      <Wrapper>
        <Row>
          {updateProId ? (
            <UpdateProduct />
          ) : (
            <>
              {products.map((product) => (
                <Col className=" col-md-4 mb-4 " key={product._id}>
                  <Card className="col-style ">
                    <span className="delete-section">
                      <CloseButton onClick={() => deleteProduct(product._id)} />
                    </span>
                    <span className="edit-section">
                      <Link
                        to={`/dashboard/MangeProducts`}
                        onClick={() => setUpdatePro(product)}
                      >
                        <i className="fa-regular fa-pen-to-square"></i>
                      </Link>
                    </span>

                    <Row className="d-flex align-items-center">
                      <Col md={4}>
                        <Card.Img variant="top" src={`/img/${product.image}`} />
                      </Col>
                      <Col md={8}>
                        <Card.Title className="text-start">
                          <h2>{product.name}</h2>
                        </Card.Title>
                        <Card.Subtitle className="mt-3 text-muted text-start">
                          {product.price} $
                        </Card.Subtitle>
                      </Col>
                    </Row>
                    <Row>
                      <Col>
                        <Card.Body className="mt-3">
                          <Card.Text>{product.description}</Card.Text>
                        </Card.Body>
                      </Col>
                    </Row>
                  </Card>
                </Col>
              ))}
            </>
          )}
        </Row>
        <AddProduct formState={formState} setForm={setForm} />
      </Wrapper>
    </>
  );
};

export default ProductsTable;
