import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { deletePro } from "../../rtk/slices/productsSlice";
import { Link } from "react-router-dom";
import Card from "react-bootstrap/Card";
import AddProduct from "../AddProduct";
import UpdateProduct from "../UpdateProduct";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import { useParams, useNavigate } from "react-router-dom";
import { useState } from "react";

import CloseButton from "react-bootstrap/CloseButton";
const ProductsTable = ({ formState, setForm }) => {
  const params = useParams();
  console.log(params.id);
  const { products } = useSelector((state) => state.products);
  const dispatch = useDispatch();
  // const [filterVal, setFilter] = useState("");

  // const productsFilter = () => {
  //   const e = products.filter((e) => e.productName.includes(filterVal));
  //   return e;
  // };

  return (
    <>
      <Row>
        {products.map((product) => (
          <Col className=" col-md-4 mb-4 " key={product.id}>
            <Card className="col-style ">
              <span className="delete-section">
                <CloseButton
                  onClick={() => {
                    dispatch(deletePro(product));
                  }}
                />
              </span>
              <span
                className="edit-section"
                // onClick={() => {
                //   setUpdate(true);
                // }}
              >
                <Link to={`/Dashboard/MangeProducts/Update/${product.id}`}>
                  <i className="fa-regular fa-pen-to-square"></i>
                </Link>
              </span>

              <Row className="d-flex align-items-center">
                <Col md={4}>
                  <Card.Img variant="top" src={product.img} />
                </Col>
                <Col md={8}>
                  <Card.Title className="text-start">
                    {product.productName}
                  </Card.Title>
                </Col>
              </Row>
              <Row>
                <Col>
                  <Card.Body className="mt-3">
                    <Card.Text>
                      Some quick example text to build on the card title and
                      make up the bulk of the card's content.
                    </Card.Text>
                    <Form>
                      <Form.Check
                        type="switch"
                        id="custom-switch"
                        label="Active"
                      />
                    </Form>
                  </Card.Body>
                </Col>
              </Row>
            </Card>
          </Col>
        ))}
      </Row>
      <AddProduct formState={formState} setForm={setForm} />
      {/* {window.location.pathname.includes("Update") && (
        <UpdateProduct
          formState={formState}
          setForm={setForm}
          updateForm={updateForm}
          setUpdate={setUpdate}
        />
      )} */}
    </>
  );
};

export default ProductsTable;
