import React, { useContext, useState } from "react";
import style from "./RegisterScreen.module.css";
import authApi from "../../api/auth";
import axios from "axios";
import UserContext from "../../context/appContext";
import {
  DISPLAY_ALERT,
  REGISTER_SUCCESS,
  CLEAR_ALERT,
} from "../../rtk/slices/authSlice";
import { useDispatch, useSelector } from "react-redux";
import AlertMsg from "../Alert";
import { useNavigate } from "react-router-dom";
import useAppContext from "../../context/context";
function RegisterScreen(props) {
  const navigate = useNavigate();

  const [data, setData] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
  });
  const dispatch = useDispatch();
  const { showAlert, alertType, alertText } = useSelector(
    (state) => state.auth
  );
  const { user, setUser } = useContext(UserContext);
  const { clearAlert } = useAppContext();

  const fetchData = async (e) => {
    e.preventDefault();
    if (
      !data.email ||
      !data.password ||
      !data.fname ||
      !data.lname ||
      (!data.email && !data.password && !data.fname && !data.lname)
    ) {
      dispatch(DISPLAY_ALERT());
    }
    try {
      const res = await axios.post("/register", data);
      dispatch(REGISTER_SUCCESS({ user: user }));
      authApi.setToken(res.data.token);
      setTimeout(() => {
        navigate("/login");
      }, 2000);
    } catch (e) {
      dispatch(
        DISPLAY_ALERT({ alertType: "danger", alertText: e.response.data.msg })
      );
      setTimeout(() => {
        dispatch(CLEAR_ALERT());
      }, 2000);
      console.log(e.response.data.msg);
    }
  };

  return (
    <>
      <form className={style.registerform} onSubmit={fetchData}>
        <h3>Sign Up</h3>
        {showAlert && <AlertMsg alertType={alertType} alertText={alertText} />}

        <div className="mb-3 text-start">
          <label>First name</label>
          <input
            type="text"
            className="form-control"
            onChange={(e) => setData({ ...data, fname: e.target.value })}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Last name</label>
          <input
            type="text"
            className="form-control"
            onChange={(e) => setData({ ...data, lname: e.target.value })}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Email address</label>
          <input
            type="email"
            className="form-control"
            onChange={(e) => setData({ ...data, email: e.target.value })}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            onChange={(e) => setData({ ...data, password: e.target.value })}
          />
        </div>

        <div className="d-grid">
          <button type="submit" className="btn btn-primary">
            Sign Up
          </button>
        </div>
        <p className="forgot-password text-right">
          Already registered? <a href="/login">Login</a>
        </p>
      </form>
    </>
  );
}

export default RegisterScreen;
