import Button from "react-bootstrap/Button";
import { useSelector } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useEffect, useState } from "react";
import { UPDATE_PRODUCT } from "../../rtk/slices/productsSlice";
import axios from "axios";
import { useDispatch } from "react-redux";
import { useAppContext } from "../../context/appContext";

function UpdatePro() {
  const params = useParams();
  const dispatch = useDispatch();
  const { UpdateItem } = useAppContext();
  const { sizes } = useSelector((state) => state.sizes);
  const { colors } = useSelector((state) => state.colors);
  const navigate = useNavigate();
  const { products } = useSelector((state) => state.products);

  const [value, setValue] = useState("");
  console.log(value.sizeList);
  const handleImage = (e) => {
    const image = e.target.files[0].name;
    const array = image.forEach((element) => {
      array.push(element.name);
    });

    setValue({ ...value, image: array });
  };
  const HandleUpdate = (e) => {
    e.preventDefault();
    UpdateItem(value);
  };
  useEffect(() => {
    (async () => {
      const res = await axios.get(`/dashboard/MangeProducts/${params.id}`);
      console.log(res);
      setValue(res.data.product);
      // getsize();
    })();
  }, [params.id]);
  return (
    <div className="editForm">
      {value && (
        <Form onSubmit={HandleUpdate}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              value={value.name}
              onChange={(e) => setValue({ ...value, name: e.target.value })}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Description</Form.Label>
            <Form.Control
              type="text"
              value={value.description}
              onChange={(e) =>
                setValue({ ...value, description: e.target.value })
              }
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Price</Form.Label>
            <Form.Control
              type="text"
              value={value.price}
              onChange={(e) => setValue({ ...value, price: e.target.value })}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            {/* <Form.Label>Categories</Form.Label>
            <Form.Control
              type="text"
              value={value.categories}
              onChange={(e) =>
                setValue({ ...value, categories: e.target.value })
              }
            /> */}
            <select
              className="input-select m-2"
              onClick={(e) =>
                setValue({ ...value, categories: e.target.value })
              }
            >
              {categories.map((category) => (
                <option value={category.categoryName}>
                  {category.categoryName}
                </option>
              ))}
            </select>
          </Form.Group>
          <h5>Sizes</h5>
          {sizes.map((size) => (
            <Form.Group className="form-check form-switch">
              <Form.Check
                className="mb-3"
                type="checkbox"
                id="custom-switch"
                label={size.sizeName}
                checked={
                  value.sizeList.filter((i) => i === size.sizeName)[0] ===
                  size.sizeName
                    ? true
                    : false
                }
                onChange={(e) => {
                  console.log(
                    value.sizeList.filter((i) => i === size.sizeName)[0]
                  );
                  setValue({ ...value, elementState: !value.elementState });
                }}
              />
            </Form.Group>
          ))}
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Image</Form.Label>
            <Form.Control
              multiple
              type="file"
              className="form-control"
              onChange={(e) => handleImage(e)}
            />
          </Form.Group>

          <Button type="submit" variant="primary">
            Submit
          </Button>
        </Form>
      )}
    </div>
  );
}

export default UpdatePro;
