import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import { useAppContext } from "../../context/appContext";
import FormRow from "../modal/FormRow";

const AddColorForm = () => {
  const { setForm, addColor } = useAppContext();

  const [data, setData] = useState({
    colorName: "",
    elementState: false,
  });

  return (
    <form
      className="editForm"
      onSubmit={(e) => addColor(e, data)}
      encType="multipart/form-data"
    >
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>
      <div className="mb-3 text-start">
        <label>Color Name</label>
        <input
          type="text"
          className="form-control"
          value={data.colorName}
          onChange={(e) => setData({ ...data, colorName: e.target.value })}
        />
      </div>
      <div className="mb-3 form-check form-switch">
        <label>State</label>

        <input
          type="checkbox"
          id="custom-switch"
          label="Active"
          className="form-check-input"
          value={data.elementState}
          onChange={(e) =>
            setData({ ...data, elementState: !data.elementState })
          }
        />
      </div>

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Color
        </button>
      </div>
    </form>
  );
};

export default AddColorForm;
