import Button from "react-bootstrap/Button";
import { useDispatch } from "react-redux";

import Form from "react-bootstrap/Form";
import { insert } from "../rtk/slices/productsSlice";
import { useEffect, useState, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";

import "./Edit.css";
function AddProduct() {
  const navigate = useNavigate();

  const dispatch = useDispatch();
  // const [productName, productNameSet] = useState("");
  // const [category, categorySet] = useState("");
  // const [price, priceSet] = useState("");
  // const [img, imageSet] = useState("");
  const productName = useRef(null);
  const category = useRef(null);
  const price = useRef(null);
  const img = useRef(null);
  // const [product, productSet] = useState({});
  // useEffect(() => {
  //   productSet(
  //     {
  //       id: Math.random(),
  //       productName,
  //       category,
  //       price,
  //       img,
  //     },
  //     []
  //   );
  // });
  const Add = () => {
    const product = {
      id: Math.random(),
      productName: productName.current.value,
      category: category.current.value,
      price: price.current.value,
      img: img.current.value,
    };
    dispatch(insert(product));
    navigate("/products");
  };

  return (
    <div className="editForm">
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>ProductName</Form.Label>
          <Form.Control
            type="text"
            // value={productName}
            ref={productName}
            // onChange={(e) => productNameSet(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Category</Form.Label>
          <Form.Control
            type="text"
            // value={category}
            ref={category}
            // onChange={(e) => categorySet(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Price</Form.Label>
          <Form.Control
            type="text"
            // value={price}
            ref={price}
            // onChange={(e) => priceSet(e.target.value)}
          />
          target
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Image</Form.Label>
          <Form.Control
            type="file"
            // value={img}
            ref={img}
            // onChange={(e) => imageSet(e.target.value)}
          />
        </Form.Group>

        <Button
          variant="primary"
          onClick={() => {
            Add();
          }}
        >
          Add
        </Button>
      </Form>
    </div>
  );
}

export default AddProduct;
