import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import CloseButton from "react-bootstrap/CloseButton";
import AddProduct from "../add/Add";
import Form from "react-bootstrap/Form";
import { useNavigate } from "react-router-dom";
import { useAppContext } from "../../context/appContext";

const SizesTable = ({ formState, setForm }) => {
  const { sizes } = useSelector((state) => state.sizes);
  const navigate = useNavigate();

  const { deleteItem, UpdateItem } = useAppContext();

  const deleteSize = async (id) => {
    try {
      await deleteItem(id);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  const HandleUpdate = (e) => {
    const data = {
      sizeName: e.sizeName,
      elementState: !e.elementState,
      _id: e._id,
    };

    UpdateItem(data);
  };
  return (
    <>
      <Table>
        <thead>
          <tr>
            {/* <th>#</th> */}
            <th>SizeName</th>
            <th>State</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {sizes.map((size) => (
            <tr key={size._id}>
              {/* <td style={{ width: " 20px" }}>{size._id}</td> */}
              <td>{size.sizeName}</td>
              <td>
                {" "}
                <Form>
                  {/* <Link to={`/dashboard/MangeSizes/UpdateState/${size._id}`}> */}
                  <Form.Check
                    type="switch"
                    id={size._id}
                    label="Active"
                    name={size.sizeName}
                    checked={size.elementState === true ? true : false}
                    onChange={(e) => HandleUpdate(size)}
                  />
                  {/* </Link> */}
                </Form>
              </td>

              <td>
                {" "}
                <span className="delete-section">
                  <CloseButton onClick={() => deleteSize(size._id)} />
                </span>
                <span className="edit-section">
                  <Link to={`/dashboard/MangeSizes/Update/${size._id}`}>
                    <i className="fa-regular fa-pen-to-square"></i>
                  </Link>
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <AddProduct formState={formState} setForm={setForm} />
    </>
  );
};

export default SizesTable;
