import React, { useState, useContext } from "react";
import { useDispatch, useSelector } from "react-redux";

import { Alert } from "reactstrap";
import "./RegisterScreen.css";
import axios from "axios";
import authApi from "../../api/auth";
import { DISPLAY_ALERT, SETUP_USER_SUCCESS } from "../../rtk/slices/authSlice";
import UserContext from "../../context/userContext";

function LoginScreen(props) {
  const { user, setUser } = useContext(UserContext);

  const { showAlert, alertType, alertText } = useSelector(
    (state) => state.auth
  );

  const [data, setData] = useState({
    email: "",
    password: "",
  });
  const dispatch = useDispatch();
  const fetchData = async (e) => {
    if (!data.email || !data.password) {
      dispatch(DISPLAY_ALERT());
    }
    e.preventDefault();
    try {
      const res = await axios.post("/login", data);
      // const data = await res.json();
      console.log(res.data);
      authApi.setToken(res.data);
      if (user) {
        dispatch(SETUP_USER_SUCCESS());
      }
    } catch (e) {
      console.log(e.response.data);
    }
  };

  return (
    <>
      <form onSubmit={fetchData}>
        <h3>Log In</h3>
        {showAlert && <Alert color={alertType}>{alertText}</Alert>}
        <div className="mb-3 text-start">
          <label>Email address</label>
          <input
            type="email"
            className="form-control"
            onChange={(e) => setData({ ...data, email: e.target.value })}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            onChange={(e) => setData({ ...data, password: e.target.value })}
          />
        </div>

        <div className="d-grid">
          <button type="submit" className="btn btn-primary">
            Log In
          </button>
        </div>
        <p className="forgot-password text-right">
          <a href="/sign-in">Reset password?</a>
        </p>
      </form>
    </>
  );
}

export default LoginScreen;
