import Button from "react-bootstrap/Button";

import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { deleteCat } from "../../rtk/slices/categoriesSlice";
import ListGroup from "react-bootstrap/ListGroup";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";

const CategoriesTable = () => {
  const dispatch = useDispatch();
  const categories = useSelector((state) => state.categories);

  return (
    // <ListGroup variant="flush">
    //   {categories.map((category) => (
    //     <ListGroup.Item key={category.id}>
    //       <Row className="text-start">
    //         <Col md={1}>{category.id}</Col>
    //         <Col>{category.categoryName}</Col>
    //       </Row>
    //     </ListGroup.Item>
    //   ))}
    // </ListGroup>
    <Table striped>
      <thead>
        <tr>
          <th>No</th>
          <th>CategortName</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {categories.map((category) => (
          <tr key={category.id}>
            <td>
              {category.id}
              {/* <img
                style={{ width: "50px", height: "50px" }}
                src={category.img}
                alt="Not Found"
              /> */}
            </td>
            <td>{category.categoryName}</td>

            <td>
              <Link to={`/Dashboard/MangeCategories`}>
                <Button
                  className="m-2"
                  variant="danger"
                  onClick={() => {
                    dispatch(deleteCat(category));
                  }}
                >
                  Delete
                </Button>
              </Link>

              <Link to={`/Update/${category.id}`}>
                <Button value={category.id} variant="success">
                  Edit
                </Button>
              </Link>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default CategoriesTable;
