import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
// import AlertMsg from "../Alert";
import { useSelector } from "react-redux";
import { useAppContext } from "../../context/appContext";
import FormRow from "../modal/FormRow";
import FormRowSelect from "../modal/FormRowSelect";
// import Wrapper from "../../Wrapper/FormRow";
import { useParams } from "react-router-dom";

const AddProductForm = () => {
  const {
    updateProId,
    isAdd,
    isEdit,
    elementState,
    price,
    selectedColorList,
    selectedSizeList,
    selectedCategories,
    description,
    name,
    image,
  } = useSelector((state) => state.products);
  const { sizes } = useSelector((state) => state.sizes);
  const { colors } = useSelector((state) => state.colors);
  const { categories } = useSelector((state) => state.categories);
  const { setForm, addItem, UpdateItem } = useAppContext();
  const params = useParams();

  let [data, setData] = useState({
    name,
    description,
    categories: selectedCategories,
    price,
    colorList: selectedColorList,
    sizeList: selectedSizeList,
    elementState,
    image,

    _id: updateProId || params.id,
  });
  const handleImage = (e) => {
    const x = e.target.files;
    const list = [];

    for (let i = 0; i < x.length; i++) {
      list.push(e.target.files[i].name);
    }

    setData({ ...data, image: list });
  };
  const setSize = (itemName, state) => {
    console.log(itemName);
    console.log(state);
    let list = [];
    console.log(data.sizeList);
    let exist = data.sizeList.find((value) => value === itemName);
    if (!exist) {
      list = data.sizeList;
      list.push(itemName);
      console.log(list);
      setData({ ...data, sizeList: list });
    } else {
      list = data.sizeList.filter((item) => itemName !== item);
      console.log(list);

      setData({ ...data, sizeList: list });
    }
  };
  const setColor = (itemName, state) => {
    let list = [];
    console.log(itemName);
    console.log(state);

    if (state) {
      list = data.colorList;
      list.push(itemName);
      setData({ ...data, colorList: list });
      console.log(data);
    } else {
      list = data.colorList.filter((item) => itemName !== item);
      setData({ ...data, colorList: list });
    }
  };
  return (
    <>
      <form
        className="editForm"
        onSubmit={(e) => {
          if (isAdd) {
            return addItem(e, data);
          } else {
            return UpdateItem(e, data);
          }
        }}
      >
        {/* {showAlert && <AlertMsg alertType={alertType} alertText={alertText} />} */}
        <h3>{isEdit ? "Edit Product" : "Add Product"}</h3>
        <span className="delete-section">
          <CloseButton
            onClick={() => {
              setForm(false);
            }}
          />
        </span>
        <FormRow
          type="text"
          name="Name"
          value={data.name}
          handleChange={(e) => setData({ ...data, name: e.target.value })}
        />
        <FormRow
          type="text"
          name="Description"
          value={data.description}
          handleChange={(e) =>
            setData({ ...data, description: e.target.value })
          }
        />
        <FormRow
          type="text"
          name="Price"
          value={data.price}
          handleChange={(e) => setData({ ...data, price: e.target.value })}
        />
        <FormRowSelect
          name="categories"
          labelText="categories"
          value={data.selectedCategories}
          handleChange={(e) => {
            setData({ ...data, categories: e.target.value });
          }}
          list={categories}
          x="categories"
          selected={selectedCategories[0]}
        />
        {/* <FormRowSelect
          name="colors"
          labelText="colors"
          value={data.colorList}
          handleChange={(e) => {
            setData({ ...data, colorList: e.target.value });
          }}
          list={colors}
          x="colors"
          selected={selectedColorList[0]}
        /> */}
        {/* <FormRowSelect
          name="sizes"
          labelText="sizes"
          value={data.categoryName}
          handleChange={(e) => {
            // setData({ ...data, sizeList: e.target.value });
            setSize(e.target.name, e.target.checked);
          }}
          list={sizes}
          x="sizes"
          selected={selectedSizeList[0]}
        /> */}
        <h5>Sizes</h5>
        {sizes.map((size, index) => (
          <FormRow
            type="checkbox"
            name={size.sizeName}
            // value={size.sizeName}
            handleChange={(e) => setSize(e.target.name, e.target.checked)}
            selected={selectedSizeList[0]}
          />
        ))}
        <h5>Colors</h5>
        {colors.map((color, index) => (
          <FormRow
            type="checkbox"
            name={color.colorName}
            // value={color.colorName}
            handleChange={(e) => setColor(e.target.name, e.target.checked)}
            selected={selectedColorList[0]}
          />
        ))}
        <h5>State</h5>
        <FormRow
          type="checkbox"
          name="Active"
          value={data.elementState}
          handleChange={(e) =>
            setData({ ...data, elementState: !data.elementState })
          }
        />
        <FormRow
          type="file"
          name="Image"
          handleChange={(e) => handleImage(e)}
        />
        <div className="d-grid">
          <button type="submit" className="btn btn-primary">
            Add Product
          </button>
        </div>
      </form>
    </>
  );
};

export default AddProductForm;
