import React, { useContext, useState, useRef } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import axios from "axios";
import { useNavigate } from "react-router-dom";
// import AlertMsg from "../Alert";
import { ADD_PRODUCT } from "../../rtk/slices/productsSlice";
import { useDispatch, useSelector } from "react-redux";
import FileBase64 from "react-file-base64";
const AddProductForm = ({ setForm }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [data, setData] = useState({
    name: "",
    description: "",
    categories: "",
    price: "",
    image: [],
  });
  const da = new FormData();
  da.append("name", data.name);
  da.append("name", data.description);
  da.append("name", data.categories);
  da.append("name", data.price);
  da.append("name", data.image);
  const handleImage = (e) => {
    const image = e.target.files[0].name;
    // const array = image.forEach((element) => {
    //   array.push(element.name);
    // });

    setData({ ...data, image: image });
  };
  const fetchData = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("/dashboard/MangeProducts/addProduct", data);
      console.log(res.data.product);
      dispatch(ADD_PRODUCT([res.data.product]));
      setForm(false);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  return (
    <form onSubmit={fetchData} encType="multipart/form-data">
      {/* {showAlert && <AlertMsg alertType={alertType} alertText={alertText} />} */}
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>

      <div className="mb-3 text-start">
        <label>Name</label>
        <input
          type="text"
          className="form-control"
          value={data.fname}
          onChange={(e) => setData({ ...data, name: e.target.value })}
        />
      </div>
      <div className="mb-3 text-start">
        <label>Description</label>
        <input
          type="text"
          className="form-control"
          value={data.description}
          onChange={(e) => setData({ ...data, description: e.target.value })}
        />
      </div>
      <div className="mb-3 text-start">
        <label>Categories</label>
        <input
          type="text"
          className="form-control"
          value={data.categories}
          onChange={(e) => setData({ ...data, categories: e.target.value })}
        />
      </div>
      <div className="mb-3 text-start">
        <label>Price</label>
        <input
          type="text"
          className="form-control"
          value={data.price}
          onChange={(e) => setData({ ...data, price: e.target.value })}
        />
      </div>
      <div className="mb-3 text-start">
        <label>Image</label>
        {/* <input
          multiple
          type="file"
          className="form-control"
          onChange={(e) => handleImage(e)}
        /> */}
        <FileBase64
          multiple={true}
          onDone={({ base64 }) => setData({ ...data, image: base64 })}
        />
      </div>
      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Product
        </button>
      </div>
    </form>
  );
};

export default AddProductForm;
