import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
// import AlertMsg from "../Alert";
import { useSelector } from "react-redux";
import { useAppContext } from "../../context/appContext";
import FormRow from "../modal/FormRow";
import FormRowSelect from "../modal/FormRowSelect";
import Wrapper from "../../Wrapper/FormRow";

const AddProductForm = () => {
  const { sizes } = useSelector((state) => state.sizes);
  const { colors } = useSelector((state) => state.colors);
  const { categories } = useSelector((state) => state.categories);
  const { addProduct, setForm } = useAppContext();

  const [data, setData] = useState({
    name: "",
    description: "",
    categories: "",
    price: "",
    colorList: [],
    sizeList: [],
    elementState: "",
    image: [],
  });
  const handleImage = (e) => {
    const x = e.target.files;
    const list = [];

    for (let i = 0; i < x.length; i++) {
      list.push(e.target.files[i].name);
    }

    setData({ ...data, image: list });
  };
  const setSize = (itemName, state) => {
    let list = [];
    if (state) {
      list = data.sizeList;
      list.push(itemName);
      setData({ ...data, sizeList: list });
    } else {
      list = data.sizeList.filter((item) => itemName !== item);
      setData({ ...data, sizeList: list });
    }
  };
  const setColor = (itemName, state) => {
    let list = [];
    if (state) {
      list = data.colorList;
      list.push(itemName);
      setData({ ...data, colorList: list });
    } else {
      list = data.colorList.filter((item) => itemName !== item);
      setData({ ...data, colorList: list });
    }
  };

  return (
    <form
      onSubmit={(e) => addProduct(e, data)}
      encType="multipart/form-data"
      className="editForm"
    >
      {/* {showAlert && <AlertMsg alertType={alertType} alertText={alertText} />} */}
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>

      <div className="mb-3 text-start">
        <label>Name</label>
        <input
          type="text"
          className="form-control"
          value={data.fname}
          onChange={(e) => setData({ ...data, name: e.target.value })}
        />
      </div>
      <div className="mb-3 text-start">
        <label>Description</label>
        <input
          type="text"
          className="form-control"
          value={data.description}
          onChange={(e) => setData({ ...data, description: e.target.value })}
        />
      </div>
      <div className="mb-3 text-start">
        <select
          className="input-select m-2"
          onClick={(e) => setData({ ...data, categories: e.target.value })}
        >
          {categories.map((category) => (
            <option value={category.categoryName}>
              {category.categoryName}
            </option>
          ))}
        </select>
      </div>
      <div className="mb-3 text-start">
        <label>Price</label>
        <input
          type="text"
          className="form-control"
          value={data.price}
          onChange={(e) => setData({ ...data, price: e.target.value })}
        />
      </div>
      <div className="row">
        <div className="col-md-6">
          {" "}
          <h5>Sizes</h5>
          {sizes.map((size) => (
            <div className="form-check form-switch">
              <label>{size.sizeName}</label>
              <input
                className="form-check-input"
                type="checkbox"
                id="custom-switch"
                name={size.sizeName}
                onChange={(e) => {
                  setSize(e.target.name, e.target.checked);
                }}
              />
            </div>
          ))}
        </div>
        <div className="col-md-6">
          {" "}
          <h5>Colors</h5>
          {colors.map((color) => (
            <div className="form-check form-switch">
              <label>{color.colorName}</label>
              <input
                className="form-check-input"
                type="checkbox"
                id="custom-switch"
                name={color.colorName}
                onChange={(e) => {
                  setColor(e.target.name, e.target.checked);
                }}
              />
            </div>
          ))}
        </div>
      </div>
      <h5>State</h5>
      <div className="mb-3 form-check form-switch">
        <label>Active</label>
        <input
          type="checkbox"
          id="custom-switch"
          label="Active"
          className="form-check-input"
          value={data.elementState}
          onChange={(e) =>
            setData({ ...data, elementState: !data.elementState })
          }
        />
      </div>

      <div className="mb-3 text-start">
        <label>Image</label>
        <input
          multiple
          type="file"
          name="image"
          className="form-control"
          onChange={(e) => handleImage(e)}
        />
      </div>
      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Product
        </button>
      </div>
    </form>
    // <Wrapper>
    //   <form className="form">
    //     {/* <h3>{isEditing ? "edit job" : "add job"}</h3> */}
    //     {/* {showAlert && <Alert />} */}
    //     <div className="form-center">
    //       {/* position */}
    //       <FormRow
    //         type="text"
    //         name="position"
    //         // value={position}
    //         // handleChange={handleJobInput}
    //       />
    //       {/* company */}
    //       <FormRow
    //         type="text"
    //         name="company"
    //         // value={company}
    //         // handleChange={handleJobInput}
    //       />
    //       {/* location */}
    //       <FormRow
    //         type="text"
    //         labelText="job location"
    //         name="jobLocation"
    //         // value={jobLocation}
    //         // handleChange={handleJobInput}
    //       />

    //       {/* job type */}
    //       <FormRowSelect
    //         name="jobType"
    //         labelText="job type"
    //         value={"categories"}
    //         onClick={(e) => setData({ ...data, categories: e.target.value })}
    //         list={categories}
    //       />
    //       {/* btn container */}
    //       <div className="btn-container">
    //         <button
    //           type="submit"
    //           className="btn btn-block submit-btn"
    //           // onClick={handleSubmit}
    //           // disabled={isLoading}
    //         >
    //           submit
    //         </button>
    //         <button
    //           className="btn btn-block clear-btn"
    //           onClick={(e) => {
    //             e.preventDefault();
    //             // clearValues();
    //           }}
    //         >
    //           clear
    //         </button>
    //       </div>
    //     </div>
    //   </form>
    // </Wrapper>
  );
};

export default AddProductForm;
