import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
export const apiSizes = createAsyncThunk("sizesSlice/apiSizes", async () => {
  const res = await fetch("http://localhost:3000/sizes");
  const data = await res.json();
  return data;
});

export const insertSize = createAsyncThunk(
  "sizesSlice/insertSize",
  async (sizeName, thunkApi) => {
    const { rejectWithValue } = thunkApi;
    try {
      const res = await fetch("http://localhost:3000/sizes", {
        method: "POST",

        body: JSON.stringify(sizeName),

        headers: { "Content-type": "application/json; charset=utf-8" },
      });

      const data = await res.json();
      return data;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);
export const updateSize = createAsyncThunk(
  "sizesSlice/updateSize",
  async (sizeName, thunkApi) => {
    const { rejectWithValue } = thunkApi;
    try {
      const res = await fetch(`http://localhost:3000/sizes/${sizeName.id}`, {
        method: "PUT",

        body: JSON.stringify(sizeName),

        headers: { "Content-type": "application/json; charset=utf-8" },
      });
      const data = await res.json();
      return data;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);
export const deleteSize = createAsyncThunk(
  "sizesSlice/deleteSize",
  async (sizeName, thunkApi) => {
    const { rejectWithValue } = thunkApi;
    console.log(sizeName);
    try {
      await fetch(`http://localhost:3000/sizes/${sizeName.id}`, {
        method: "DELETE",

        headers: { "Content-type": "application/json; charset=utf-8" },
      });
      return sizeName;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);
const sizesSlice = createSlice({
  name: "sizesSlice",
  initialState: { sizes: [] },
  reducers: {
    GET_SIZES: (state, action) => {
      state.sizes = action.payload;
    },
    ADD_SIZE: (state, action) => {
      state.sizes.push(...action.payload);
    },
  },
  extraReducers: (builder) => {
    builder.addCase(apiSizes.fulfilled, (state, action) => {
      return action.payload;
    });
    builder.addCase(insertSize.fulfilled, (state, action) => {
      state.push(action.payload);
    });
    builder.addCase(deleteSize.fulfilled, (state, action) => {
      return (state = state.filter(
        (sizeName) => sizeName.id !== action.payload.id
      ));
    });

    builder.addCase(updateSize.fulfilled, (state, action) => {
      const { id, sizeName, elementState } = action.payload;
      const updateState = state.find((size) => size.id === id);
      console.log(action.payload.img);
      if (updateState) {
        updateState.sizeName = sizeName;
        updateState.elementState = elementState;
      }
    });
  },
});

export const { GET_SIZES } = sizesSlice.actions;
export default sizesSlice.reducer;
