import Button from "react-bootstrap/Button";
import { useSelector } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useState } from "react";
// import { update } from "../../rtk/slices/productsSlice";
import axios from "axios";

function UpdatePro() {
  const params = useParams();
  const navigate = useNavigate();
  const { products } = useSelector((state) => state.products);
  const selectedProduct = products.filter((user) => user._id === params.id);
  const { name, description, categories, price, image } = selectedProduct[0];
  const [value, setValue] = useState({
    name,
    price,
    description,
    categories,
    image,
    id: params.id,
  });
  const handleImage = (e) => {
    const image = e.target.files[0].name;
    // const array = image.forEach((element) => {
    //   array.push(element.name);
    // });

    setValue({ ...value, image: image });
  };
  const HandleUpdate = async (e) => {
    e.preventDefault();
    console.log("res.data.color");

    try {
      const res = await axios.post(
        `/dashboard/MangeProducts/Update/${value.id}`,
        value
      );

      navigate("/dashboard/MangeProducts");
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  return (
    <div className="editForm">
      <Form onSubmit={HandleUpdate}>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            value={value.name}
            onChange={(e) => setValue({ ...value, name: e.target.value })}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Description</Form.Label>
          <Form.Control
            type="text"
            value={value.description}
            onChange={(e) =>
              setValue({ ...value, description: e.target.value })
            }
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Price</Form.Label>
          <Form.Control
            type="text"
            value={value.price}
            onChange={(e) => setValue({ ...value, price: e.target.value })}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Categories</Form.Label>
          <Form.Control
            type="text"
            value={value.categories}
            onChange={(e) => setValue({ ...value, categories: e.target.value })}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Image</Form.Label>
          <Form.Control
            multiple
            type="file"
            className="form-control"
            onChange={(e) => handleImage(e)}
          />
        </Form.Group>

        <Button type="submit" variant="primary">
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default UpdatePro;
