import React, { useState } from "react";
import { useSelector } from "react-redux";
import AlertMsg from "../AlertMsg";
import { useAppContext } from "../../context/appContext";
import Wrapper from "../../Wrapper/Register";

function NewPassword(props) {
  const { resetPassword } = useAppContext();

  const { showAlert, alertType, alertText } = useSelector(
    (state) => state.auth
  );

  const [data, setData] = useState({
    oldPassword: "",
    newPassword: "",
  });

  return (
    <>
      <Wrapper>
        <form
          className="registerform "
          onSubmit={(e) => resetPassword(e, data)}
        >
          {showAlert && (
            <AlertMsg alertType={alertType} alertText={alertText} />
          )}
          <div className="mb-3 text-start">
            <label>Write your old password</label>
            <input
              type="password"
              className="form-control"
              onChange={(e) =>
                setData({ ...data, oldPassword: e.target.value })
              }
            />
          </div>
          <div className="mb-3 text-start">
            <label>Write your new password</label>
            <input
              type="password"
              className="form-control"
              onChange={(e) =>
                setData({ ...data, newPassword: e.target.value })
              }
            />
          </div>
          <div className="d-grid">
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </div>
        </form>
      </Wrapper>
    </>
  );
}

export default NewPassword;
