import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import { useAppContext } from "../../context/appContext";
import FormRow from "../modal/FormRow";
import { useSelector } from "react-redux";

const AddColorForm = () => {
  const { setForm, addColor } = useAppContext();
  const { updateColorId } = useSelector((state) => state.sizes);
  const [data, setData] = useState({
    colorName: "",
    elementState: false,
  });

  return (
    <form
      className="editForm"
      onSubmit={(e) => addColor(e, data)}
      encType="multipart/form-data"
    >
      <h3>{updateColorId ? "edit job" : "add job"}</h3>

      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>
      <FormRow
        type="text"
        name="Color Name"
        value={data.colorName}
        handleChange={(e) => setData({ ...data, colorName: e.target.value })}
      />
      <h5>State</h5>
      <FormRow
        type="checkbox"
        name="Active"
        value={data.elementState}
        handleChange={(e) =>
          setData({ ...data, elementState: !data.elementState })
        }
      />

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Color
        </button>
      </div>
    </form>
  );
};

export default AddColorForm;
