import { createSlice } from "@reduxjs/toolkit";
const cartSlice = createSlice({
  name: "cartSlice",
  initialState: { cart: [] },
  reducers: {
    GET_CART: (state, action) => {
      state.added = false;

      state.cart = action.payload;
    },
    addToCart: (state, action) => {
      state.added = true;
      const finded = state.cart.find(
        (product) =>
          product.productId._id === action.payload.productId._id &&
          product.size === action.payload.selectedSize &&
          product.color === action.payload.selectedColor
      );
      if (finded) {
        console.log(action.payload.quantity);
        finded.quantity += action.payload.quantity;
      } else {
        console.log(finded);
        if (action.payload.quantity) {
          const cart = { ...action.payload };
          state.cart.push(cart);
        } else {
          const cart = { ...action.payload, quantity: 1 };
          state.cart.push(cart);
        }
      }
    },
    deleteCart: (state, action) => {
      state.item = true;

      state.cart.filter((el) => el._id !== action.payload);
    },
    clearCart: (state, action) => {
      return (state.cart = []);
    },
    updateCart: (state, action) => {
      const { _id, name, price, categories } = action.payload;
      const updateState = state.cart.find(
        (product) => product._id.toString() === _id.toString()
      );
      if (updateState) {
        updateState.name = name;
        updateState.price = price;
        updateState.categories = categories;
      }
    },
  },
});

export const { addToCart, deleteCart, clearCart, updateCart, GET_CART } =
  cartSlice.actions;
export default cartSlice.reducer;
