import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import { useAppContext } from "../../context/appContext";
import FormRow from "../modal/FormRow";
import { useSelector } from "react-redux";

const AddSizeForm = () => {
  const { sizes, updateSizeId } = useSelector((state) => state.sizes);

  const selectedSize = sizes.filter((user) => user._id === updateSizeId);
  const { sizeName, elementState } = selectedSize[0];
  const [data, setData] = useState({
    sizeName,
    elementState,
    _id: updateSizeId,
  });

  const { UpdateItem } = useAppContext();

  const { setForm, addSize } = useAppContext();
  // const [data, setData] = useState({
  //   sizeName: "",
  //   elementState: false,
  // });

  return (
    <form
      className="editForm"
      onSubmit={(e) => addSize(e, data)}
      encType="multipart/form-data"
    >
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>
      <FormRow
        type="text"
        name="Size Name"
        value={data.sizeName}
        handleChange={(e) => setData({ ...data, sizeName: e.target.value })}
      />
      <h5>State</h5>
      <FormRow
        type="checkbox"
        name="Active"
        value={data.elementState}
        handleChange={(e) =>
          setData({ ...data, elementState: !data.elementState })
        }
      />

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Size
        </button>
      </div>
    </form>
  );
};

export default AddSizeForm;
