import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { deleteCat } from "../../rtk/slices/categoriesSlice";

import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import CloseButton from "react-bootstrap/CloseButton";
import AddProduct from "../add/Add";
import Form from "react-bootstrap/Form";
import { updateCat } from "../../rtk/slices/categoriesSlice";
import { useNavigate } from "react-router-dom";
import { useRef } from "react";
const CategoriesTable = ({ formState, setForm }) => {
  const categories = useSelector((state) => state.categories);
  console.log(categories);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const ref = useRef(null);
  const HandleUpdate = (e) => {
    dispatch(
      updateCat({
        id: e.id,
        categoryName: e.name,
        elementState: e.checked,
      })
    );
    navigate("/dashboard/MangeCategories");
  };
  return (
    <>
      <Table>
        <p>categories</p>
        <thead>
          <tr>
            <th>#</th>
            <th>Category Name</th>
            <th>State</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category) => (
            <tr key={category._id}>
              <td style={{ width: " 20px" }}>{category._id}</td>
              <td>{category.categoryName}</td>

              <td>
                {" "}
                <Form>
                  <Link
                    to={`/dashboard/MangeCategories/UpdateState/${category._id}`}
                  >
                    <Form.Check
                      type="switch"
                      id={category._id}
                      ref={ref}
                      label="Active"
                      name={category.categoryName}
                      checked={category.elementState === true ? true : false}
                      onChange={(e) => HandleUpdate(e.target)}
                    />
                  </Link>
                </Form>
              </td>
              <td>
                {" "}
                <span className="delete-section">
                  <CloseButton
                    onClick={() => {
                      dispatch(deleteCat(category));
                    }}
                  />
                </span>
                <span className="edit-section">
                  <Link to={`/dashboard/MangeCategories/Update/${category.id}`}>
                    <i className="fa-regular fa-pen-to-square"></i>
                  </Link>
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <AddProduct formState={formState} setForm={setForm} />
    </>
  );
};

export default CategoriesTable;
