import { useDispatch } from "react-redux";
import React, { useContext } from "react";
import {
  CLEAR_ALERT,
  DISPLAY_ALERT,
  REGISTER_SUCCESS,
} from "../rtk/slices/authSlice";
import { useNavigate } from "react-router-dom";

const AppContext = React.createContext();

const AppProvider = ({ children }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const clearAlert = () => {
    setTimeout(() => dispatch(CLEAR_ALERT()), 2000);
  };

  const displayAlert = (msg) => {
    dispatch(DISPLAY_ALERT({ alertText: msg }));
    clearAlert();
  };
  const successRegister = (user) => {
    dispatch(REGISTER_SUCCESS({ user: user }));
    clearAlert();

    setTimeout(() => {
      navigate("/login");
    }, 2000);
  };

  getAllItems = async () => {
    const products = await axios.get("/dashboard/MangeProducts");
    dispatch(ADD_PRODUCT(products.data.products));
    const categories = await axios.get("/dashboard/MangeCategories");
    dispatch(ADD_Category(categories.data.categories));

    const colors = await axios.get("/dashboard/MangeColors");
    dispatch(ADD_Color(colors.data.colors));

    const sizes = await axios.get("/dashboard/MangeSizes");
    dispatch(ADD_Size(sizes.data.sizes));
  };
  return (
    <AppContext.Provider value={{ displayAlert, successRegister }}>
      {children}
    </AppContext.Provider>
  );
};

const useAppContext = () => {
  return useContext(AppContext);
};

export { useAppContext, AppProvider };
