import Button from "react-bootstrap/Button";
import { useDispatch } from "react-redux";
import "./AddProduct.css";
import Form from "react-bootstrap/Form";
import { insert } from "../rtk/slices/productsSlice";
import { insertCategory } from "../rtk/slices/categoriesSlice";
import { useRef } from "react";
import { useNavigate } from "react-router-dom";
import CloseButton from "react-bootstrap/CloseButton";
import ReactDOM from "react-dom";

import "./Edit.css";
function AddProduct({ formState, setForm }) {
  const navigate = useNavigate();

  const dispatch = useDispatch();

  const productName = useRef(null);
  const category = useRef(null);
  const price = useRef(null);
  const img = useRef(null);
  const categoryName = useRef(null);
  const categoryImg = useRef(null);
  console.log(img);
  const AddProduct = () => {
    const product = {
      productName: productName.current.value,
      category: category.current.value,
      price: price.current.value,
      img: "./img/" + img.current.value.slice(12),
    };

    dispatch(insert(product));
    navigate("/Dashboard/MangeProducts");
  };
  const AddCategory = () => {
    const category = {
      categoryName: categoryName.current.value,

      img: "./img/" + categoryImg.current.value.slice(12),
    };

    dispatch(insertCategory(category));
    navigate("/controlPanel?addedCategory");
  };
  const Overlay = ({ closeModal }) => (
    <div className="overlay" onClick={closeModal}></div>
  );
  const BackDrop = ({ children }) => <div>{children}</div>;
  return (
    formState && (
      <>
        {ReactDOM.createPortal(
          <>
            <Overlay />
            <BackDrop>
              {" "}
              <div className="addForm">
                {/* {window.location.pathname === "/AddProduct" ? ( */}
                <Form>
                  <span className="delete-section">
                    <CloseButton
                      onClick={() => {
                        setForm(false);
                      }}
                    />
                  </span>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>ProductName</Form.Label>
                    <Form.Control type="text" ref={productName} />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Category</Form.Label>
                    <Form.Control type="text" ref={category} />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Price</Form.Label>
                    <Form.Control type="text" ref={price} />
                    target
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Image</Form.Label>
                    <Form.Control type="file" ref={img} />
                  </Form.Group>

                  <Button
                    variant="primary"
                    onClick={() => {
                      AddProduct();
                    }}
                  >
                    Add
                  </Button>
                </Form>
                {/* ) : ( */}
                {/* <Form>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Category</Form.Label>
            <Form.Control type="text" ref={categoryName} />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Image</Form.Label>
            <Form.Control type="file" ref={categoryImg} />
          </Form.Group>
          <Button
            variant="primary"
            onClick={() => {
              AddCategory();
            }}
          >
            Add
          </Button>
        </Form>
      )} */}
              </div>
            </BackDrop>
          </>,
          document.getElementById("modal")
        )}
      </>
    )
  );
}

export default AddProduct;
